INSERT INTO `assessmentTemplates` (`id`, `name`, `careSettingType`, `description`, `isDefault`, `createdAt`, `updatedAt`) VALUES (10, 'Nursing Home', 'nursing', 'Comprehensive assessment template for nursing homes providing 24-hour nursing care. Includes additional focus on clinical care, medication management, registered nurse requirements, health monitoring, and complex care needs.', 1, '2025-12-15 02:43:11', '2025-12-15 02:43:11');
INSERT INTO `assessmentTemplates` (`id`, `name`, `careSettingType`, `description`, `isDefault`, `createdAt`, `updatedAt`) VALUES (11, 'Domiciliary Care (Home Care)', 'domiciliary', 'Assessment template for domiciliary care services providing care and support in people\'s own homes. Emphasises lone working safety, travel time, person-centred care planning, and supporting independence in familiar surroundings.', 1, '2025-12-15 02:43:11', '2025-12-15 02:43:11');
INSERT INTO `assessmentTemplates` (`id`, `name`, `careSettingType`, `description`, `isDefault`, `createdAt`, `updatedAt`) VALUES (12, 'Supported Living', 'supported_living', 'Assessment template for supported living services helping people live independently in their own tenancies. Focuses on promoting independence, community access, life skills development, tenancy rights, and personalised support.', 1, '2025-12-15 02:43:11', '2025-12-15 02:43:11');
