INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30308, 14, '14.4', 'There are mechanisms in place for feedback to be obtained from people, staff, professionals, and family members.', 'Evidence required: There was evidence of client surveys completed in September (the year completed was not stipulated)...', NULL, NULL, 'There are mechanisms in place for feedback to be obtained from people, staff, professionals, and family members', '2025-12-14 18:13:18', NULL, 'Feedback mechanisms in place showing: methods for gathering feedback from service users, families, staff, and professionals, and evidence feedback is actively sought.', 'Feedback systems including: satisfaction surveys (annual for service users and families), residents\' meetings (monthly minimum), suggestion boxes available, feedback forms accessible, staff meetings and surveys, professional feedback sought (commissioners, healthcare professionals), complaints procedure accessible, compliments recording system, digital feedback options, accessible formats available (easy read, large print).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30309, 14, '14.5', 'There is evidence that feedback sought from people, staff, professionals, and family members are analysed and acted upon (You Said We Did).', 'Evidence required: There is evidence that feedback has been sought from some groups that utilise the service and that t...', NULL, NULL, 'There is evidence that feedback sought from people, staff, professionals, and family members are analysed and acted upon (You Said We Did)', '2025-12-14 18:13:18', NULL, 'Feedback analysis and action records showing: feedback collected is reviewed, themes identified, and actions taken in response to feedback.', 'Feedback analysis documentation containing: survey results analysed (satisfaction scores, themes identified), residents\' meeting minutes showing issues raised and actions agreed, \"you said, we did\" displays showing feedback and responses, complaints analysis (themes, trends, improvements), staff feedback review, professional feedback considered, action plans developed from feedback, evidence of service improvements implemented, governance reporting on feedback.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30310, 14, '14.6', 'There is documented evidence of planned meetings held, appropriate actions taken, and minutes circulated to all parties.', 'Evidence required: There is evidence that meetings are undertaken regularly with staff...', NULL, NULL, 'There is documented evidence of meetings with people, friends, family and staff and that there is appropriate actions taken, and minutes available to all parties. (at least quarterly)', '2025-12-14 18:13:18', NULL, 'Meeting records showing: planned meetings held as scheduled, appropriate attendees present, actions agreed and documented, and follow-up of actions.', 'Meeting documentation containing: residents\' meetings (monthly, minutes recorded, attendance noted), staff meetings (monthly minimum, agenda and minutes), management meetings (weekly/monthly), health and safety meetings, infection control meetings, safeguarding meetings, meeting schedules published, actions recorded with responsible persons and deadlines, action tracking and completion monitoring, previous actions reviewed at next meeting.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30311, 14, '14.7', 'There is documented evidence of an audit schedule which clearly identifies the frequency of audits undertaken.', 'Evidence required: there was no evidence an audit schedule being in place on review...', NULL, NULL, 'There is a documented audit schedule in place which is adhered to', '2025-12-14 18:13:18', NULL, 'Audit schedule documenting: planned audits for the year, audit frequency defined, responsible persons assigned, and schedule is comprehensive covering all key areas.', 'Annual audit schedule containing: all audit types listed (care plans, medication, infection control, health and safety, safeguarding, dignity, environment, catering, records), audit frequency specified (monthly, quarterly, annual), responsible persons assigned, audit dates planned, schedule covers all CQC key lines of enquiry, schedule approved by management, schedule displayed/accessible to staff, schedule reviewed and updated annually.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30312, 14, '14.8', 'There is evidence that a range of audits are completed which contributes to the ongoing service development/outcomes:\n\tCare plans\n\tMedicines\n\tDaily notes and intervention charts\n\tStaff files\n\tFinances\n\tInfection control\n\tHealth and safety\n\tWeight analysis\n\tSkin integrity\n\tKitchen/catering\n\tDining experience\n\tManagers walkaround\n\tNight visits\n\tCall bell analysis\n\tMissed and late calls\n\tSenior Manager Quality assurance audit\n\tDignity Audit\n\tNMC checks\n\tDriving licence and insura', 'on review we did evidence some audits being carried out but not for all areas expected. For example: -\n\tCare plans. The system stated that 6 care files had been audited but there was no evidence or detail around what had been audited and if there were any actions as a result.', NULL, NULL, 'All audits relevant to the service are undertaken as per the audit schedule', '2025-12-14 18:13:18', NULL, 'Audit completion records showing: range of audits conducted covering all aspects of service quality, audit findings documented, and evidence audits contribute to quality improvement.', 'Audit programme evidence showing: variety of audits completed (care plans, medication, IPC, health and safety, dignity, nutrition, activities, environment, records), audit reports with findings and scores, audit schedule compliance monitored, audit findings analysed, trends identified, good practice celebrated, areas for improvement identified, audit results inform quality improvement plans, governance oversight of audit programme.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30313, 14, '14.9', 'There is evidence that all identified areas for improvement have an associated action plan which details the action to be completed, who is responsible and timescale for completion. Action is signed off when completed.', 'There was some evidence that actions had been identified and listed on some audits undertaken, however there was not an overarching action plan in place to evidence continual improvement that detailed who was responsible and a timescale for completion.', NULL, NULL, 'There is an action plan that covers all areas as above and meets the agreed timescales.', '2025-12-14 18:13:18', NULL, 'Action plans showing: improvements identified from audits/incidents/feedback have action plans, actions are SMART (Specific, Measurable, Achievable, Relevant, Time-bound), and progress is monitored.', 'Action planning documentation containing: action plans for all identified improvements, SMART actions (specific task, measurable outcome, assigned person, realistic timescale, relevant to issue), action tracking system (RAG rated - Red/Amber/Green), progress monitoring (monthly reviews), evidence of actions completed, re-audit showing improvements, governance oversight of action plans, accountability for completion, escalation if actions overdue.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30314, 14, '14.10', 'Accidents and incidents, escalated appropriately with actions and trends identified.', 'Evidence required: There was a process in place that identified Accidents and incidents, and these were logged on the e...', NULL, NULL, 'Accident and incidents are appropriately recorded and escalated.  There is evidence of documented trend analysis and action taken to reduce the level of risk and this has been re-evaluated to measure the effectiveness of actions taken and if it has mitigated risk.', '2025-12-14 18:13:18', NULL, 'Accident and incident management showing: all incidents reported and investigated, appropriate escalation (safeguarding, CQC, commissioners), actions taken, and trend analysis conducted.', 'Incident management system containing: incident report forms for all accidents/incidents, investigation completed (root cause analysis), immediate actions documented, escalation to safeguarding if required, serious incidents reported to CQC, commissioner notification, family notification, actions to prevent recurrence, monthly incident analysis (types, trends, themes), learning shared with staff, governance reporting, evidence of reduced incidents following actions.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30315, 14, '14.2', 'There is clear evidence that lessons learned have been shared with relevant people ', 'Evidence required: We did see some evidence of lesson learned being discussed in supervisions, but this requires streng...', NULL, NULL, 'Evidence of lessons learnt and this has been shared appropriately with the relevant people', '2025-12-14 18:13:18', NULL, 'Learning and improvement records showing: lessons from incidents/complaints/audits identified, learning shared with relevant people, and evidence practice has improved.', 'Learning dissemination evidence showing: lessons learned log maintained, staff meeting minutes sharing learning from incidents, training provided based on learning needs, policy/procedure updates following incidents, learning bulletins or newsletters, supervision discussions about learning, re-audit showing improvements embedded, reduced repeat incidents, improved audit scores, external learning shared (safeguarding adult reviews, CQC reports, sector guidance).');
