INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30355, 21, '21.5', 'Service users and families feel their diversity is respected and valued.', 'Feedback and surveys show positive experiences of inclusive care.', NULL, NULL, 'Service user and family feedback', '2025-12-14 18:13:18', NULL, 'Service user and family feedback on inclusive care showing: satisfaction with respect for diversity, cultural needs met, discrimination concerns addressed, and feeling valued.', 'Feedback evidence showing: satisfaction survey results on respect and dignity, feedback from service users and families about cultural/religious needs being met, residents\' meeting discussions about diversity and inclusion, compliments about inclusive care, any discrimination concerns raised and actions taken, advocacy involvement for minority groups, evidence people feel their identity is respected and valued.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30356, 22, '22.1', 'Assess management oversight, governance meetings, accountability frameworks, and leadership quality. Check registered manager presence and oversight.', 'Governance meetings are held regularly with clear accountability structures.', NULL, NULL, 'Governance meeting minutes and accountability frameworks', '2025-12-14 18:13:18', NULL, 'Governance structure and meeting records showing: management meetings, board/trustee meetings, accountability frameworks, decision-making processes, and strategic oversight.', 'Governance documentation containing: organisational structure chart showing reporting lines, management meeting minutes (weekly/monthly), board/trustee meeting minutes (quarterly minimum), terms of reference for governance meetings, accountability framework (who is responsible for what), decision-making records, strategic planning documents, risk register reviewed at governance level, quality and safety reporting to board.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30357, 22, '22.2', 'Registered manager is present, visible, and provides effective leadership.', 'Staff and service users report regular interaction with registered manager.', NULL, NULL, 'Leadership presence and feedback', '2025-12-14 18:13:18', NULL, 'Registered manager presence and leadership evidence showing: CQC registration, visibility in service, staff supervision, leadership style, and stakeholder feedback on leadership quality.', 'Leadership evidence showing: registered manager CQC registration certificate (current), duty rotas showing regular presence in service, staff supervision records (registered manager conducting supervisions), leadership walkabouts and visibility, staff feedback on leadership (accessible, supportive, visible), service user/family feedback on management, leadership style promoting open culture, registered manager involvement in incidents/safeguarding/quality issues.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30358, 22, '22.3', 'Quality assurance systems are in place and effective.', 'Audit schedules show regular quality monitoring and improvement actions.', NULL, NULL, 'Quality assurance records and improvement plans', '2025-12-14 18:13:18', NULL, 'Quality assurance systems and continuous improvement evidence showing: audit schedule, quality monitoring, performance indicators, improvement plans, and governance oversight of quality.', 'Quality assurance framework containing: annual audit schedule with completion records, quality monitoring tools and results, key performance indicators tracked (falls, pressure ulcers, complaints, safeguarding, staff turnover, training compliance), quality improvement plans with progress monitoring, governance oversight of quality (board reporting, quality committee), external quality reviews (commissioner visits, peer reviews), CQC rating and improvement actions, benchmarking against sector standards, continuous improvement culture.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60001, 23, '23.1', 'There is an up-to-date recruitment policy and procedure in place.', 'Copy of recruitment policy showing review date within last 12-24 months, or evidence of annual policy review schedule.', NULL, NULL, 'Recruitment policy document dated within last 2 years, policy review log, or board meeting minutes approving updated recruitment procedures.', '2025-12-15 01:45:40', NULL, 'Pre-admission assessment and decision record', 'Assessment document containing: physical health needs and medical history, mental health and cognitive status, communication needs and preferences, mobility and falls risk assessment, nutrition and hydration needs, personal care requirements, social and emotional needs, cultural and religious preferences, capacity assessment for key decisions, risk assessments (falls, choking, skin integrity, behaviour), family/representative involvement, funding arrangements confirmed, decision that service can meet needs, assessment date and assessor signature');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60002, 23, '23.12', 'The recruitment process is in line with the Equality Act 2010.', 'Documentation showing equal opportunities monitoring, reasonable adjustments process, and non-discriminatory selection criteria.', NULL, NULL, 'Equal opportunities monitoring forms, interview scoring sheets with objective criteria, reasonable adjustments policy, or diversity and inclusion statement in recruitment materials.', '2025-12-15 01:45:40', NULL, 'Individual care and support plan', 'Care plan containing: person\'s goals and desired outcomes, daily routine preferences (wake time, meal times, bedtime), communication preferences and methods, cultural and religious needs with specific requirements, how independence will be maintained and promoted, specific interventions for each identified need, risk management strategies agreed with person, equipment or adaptations required, professional involvement (GP, district nurse, OT), review date set, person/representative involvement in planning, care plan can be first person (\'I prefer...\') or third person as long as person-centred');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60003, 23, '23.23', 'There is evidence of an application form which includes full employment history and that any gaps in employment have been explored and recorded.', 'Completed application forms (not just CVs) showing continuous employment history from leaving education, with written explanations for any gaps exceeding one month.', NULL, NULL, 'Application form with employment history section completed in full, interview notes documenting discussion of employment gaps, or signed statement from candidate explaining gaps in employment.', '2025-12-15 01:45:40', NULL, 'Care plan review records', 'Review documentation showing: review date (minimum monthly or as per care plan), person/representative participation in review, progress towards goals assessed, changes in needs or preferences identified, care plan updated accordingly with date, new or changed risks assessed, professional input documented (GP visit, therapy input), health monitoring reviewed (weight, vital signs, MUST score), next review date agreed, reviewer signature, evidence person\'s views considered');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60004, 23, '23.28', 'There is a documented record of interviews having taken place and by two people who are appropriately positioned in the organisation.', 'Interview notes or scoring sheets signed and dated by two interviewers, with evidence that interviewers hold appropriate positions (e.g., registered manager, senior care staff, or directors).', NULL, NULL, 'Interview assessment forms with two signatures, interview question template with candidate responses recorded, or panel interview notes showing multiple interviewers\' observations.', '2025-12-15 01:45:40', NULL, 'Mental capacity assessment for specific decisions', 'Capacity assessment record containing: specific decision being assessed (not general capacity), two-stage test applied (does person have impairment/disturbance of mind or brain, does this prevent them making this specific decision), evidence person can/cannot understand, retain, weigh and communicate decision, steps taken to support decision-making (accessible information, timing, location, support person), assessment outcome with rationale, assessor name and qualification, assessment date, review date if capacity may fluctuate, best interests process if lacking capacity');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60005, 23, '23.29', 'There is a scoring mechanism in place with an agreed appointment threshold.', 'Interview scoring matrix or rubric showing how candidates are assessed against specific criteria, with documented minimum score required for appointment.', NULL, NULL, 'Interview scoring sheet with numerical ratings for each competency, appointment threshold policy (e.g., \'candidates must score 70% or above\'), or interview panel decision form showing scores and appointment recommendation.', '2025-12-15 01:45:40', NULL, 'Consent records for care and treatment', 'Consent documentation showing: what person is consenting to (specific care or treatment), information provided about purpose, risks and benefits, person\'s understanding confirmed, voluntary consent without coercion or undue influence, capacity to consent assessed, consent form signed and dated by person (or best interests decision if lacking capacity), ongoing consent reviewed and recorded, right to withdraw consent explained, consent reviewed if care needs change, note: consent doesn\'t require signature if person lacks capacity or unable to sign, as long as involvement and agreement documented');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60006, 23, '23.30', 'There is relevant identification documentation that has been validated (signed and dated as a true likeness).', 'Photocopies of identity documents (passport, driving licence, or national ID card) with verifier\'s signature, date, and statement confirming original document seen and photograph matches applicant.', NULL, NULL, 'Copy of passport photo page with annotation \'Original seen, true likeness verified - [signature] [date]\', or identity verification checklist completed and signed by recruiting manager.', '2025-12-15 01:45:40', NULL, 'Risk assessments and management plans', 'Risk assessment documents for: falls risk (using validated tool with score), skin integrity and pressure ulcer risk (Waterlow or similar), nutrition risk (MUST tool scored monthly minimum), moving and handling assessment, choking risk, behaviours that challenge, going out in community, self-neglect, financial abuse vulnerability; Each assessment showing: risk factors identified, risk level/score, control measures and interventions, equipment required, person\'s views on managing risk, positive risk-taking balanced with safety, review frequency, assessment date and assessor');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60007, 23, '23.31', 'Is this staff member employed as: (a) Permanent staff, (b) Agency staff, or (c) Bank staff?', 'Employment contract, agency booking confirmation, or bank staff agreement clearly stating employment status.', NULL, NULL, 'Signed contract of employment for permanent staff, agency worker profile from supplying agency, or internal bank staff registration form. Answer determines which subsequent questions apply.', '2025-12-15 01:45:40', NULL, 'Communication assessment and plan', 'Communication profile containing: person\'s preferred communication methods, sensory impairments (hearing, vision) and aids used, speech and language needs, understanding and comprehension level, non-verbal communication, communication aids or technology, how person expresses pain or distress, how to gain person\'s attention, best times for communication, involvement of speech and language therapist if needed, accessible information standard compliance, staff guidance on communicating effectively with person');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60008, 23, '23.32', 'For permanent staff: Is this staff member (a) a British or Irish citizen with indefinite right to work, (b) a sponsored worker on a Health and Care Worker visa, or (c) holding another visa type with limited right to work?', 'Documentation proving right to work status - either passport showing British/Irish citizenship, share code for online right to work check, or visa/BRP card showing current immigration status.', NULL, NULL, 'British passport, Irish passport card, Home Office share code check printout, Health and Care Worker visa in passport, or Biometric Residence Permit (BRP) card copy. Answer determines additional checks required.', '2025-12-15 01:45:40', NULL, 'Nutrition and hydration care plan', 'Nutrition plan containing: dietary preferences and dislikes, cultural or religious dietary requirements, food allergies and intolerances, texture modification (soft, pureed, thickened fluids) with SALT assessment, assistance required with eating and drinking, adaptive equipment (plate guards, adapted cutlery), food fortification or supplements, MUST score and monitoring frequency, weight monitoring schedule, fluid intake monitoring if required, mealtime environment preferences, involvement of dietitian if needed');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60009, 23, '23.33', 'There is proof of right to work documentation in the UK that has been validated (signed and dated as the original seen).', 'Copy of right to work documents with verifier\'s signature and date confirming original documents inspected and validated as genuine. For online checks, printout of share code check result.', NULL, NULL, 'Passport copy with \'Original seen and verified - [signature] [date]\', Home Office online right to work check printout with employer\'s records, or BRP card copy with validation statement.', '2025-12-15 01:45:40', NULL, 'Personal care plan', 'Personal care plan containing: bathing/showering preferences (frequency, time of day, bath or shower), continence needs and management (pads, catheter, toileting routine), skin care requirements, oral hygiene and dental care, hair care preferences, nail care, dressing preferences and level of support needed, cultural or religious requirements for personal care, same gender care preferences, privacy and dignity maintained, person\'s independence promoted, equipment required (hoist, bath lift, commode)');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60010, 23, '23.2', 'There is an enhanced DBS prior to commencement of employment or evidence of an Adult First check with appropriate risk assessment.', 'Copy of enhanced DBS certificate, DBS update service check record, or Adult First barred list check with risk assessment if DBS not yet received.', NULL, NULL, 'Enhanced DBS certificate showing issue date before start date, DBS update service online check printout, or Adult First check with signed risk assessment and supervision plan pending full DBS.', '2025-12-15 01:45:40', NULL, 'Mobility and moving and handling plan', 'Moving and handling assessment containing: mobility level and aids used (frame, stick, wheelchair), transfers (bed, chair, toilet, car) with equipment and number of staff, walking ability and distance, falls risk and history, moving and handling equipment required (hoist type and sling size, slide sheets, transfer board), safe working load checked, staff training in equipment use, person\'s cooperation and understanding, environmental factors, physiotherapy or OT involvement, assessment review date');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60011, 23, '23.3', 'There is a certificate of good conduct/overseas police check from country of origin where applicable (if staff member has lived outside UK for 12+ consecutive months since age 18).', 'Certificate of good conduct or overseas police check from each country where staff member resided for 12+ months, OR documented evidence of attempts to obtain with risk assessment if unobtainable.', NULL, NULL, 'Translated and certified overseas police certificate, certificate of good conduct from embassy, or correspondence showing attempts to obtain (with risk assessment) if country does not issue such certificates.', '2025-12-15 01:45:40', NULL, 'Induction checklist and competency sign-off records', 'Induction programme showing: organisational policies covered (safeguarding, health & safety, infection control, confidentiality, whistleblowing), shadowing hours completed with experienced staff, competency assessments in key tasks with supervisor sign-off, probationary review meetings scheduled, Care Certificate enrolment or completion certificate, employee and manager signatures confirming completion');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60012, 23, '23.4', 'There are two written references, one of which is from the most recent employer, that have been received prior to commencement of employment and have been verified. (If the referees are not from a care provider, and the candidate has previously worked in care, a reference has been sought from a care employer.)', 'Two written references on headed paper or verified email, with evidence of verification (phone call notes, email confirmation, or verifier signature). One reference must be from most recent employer. If candidate has care experience, at least one reference must be from a care setting.', NULL, NULL, 'Reference letters with verification notes (\'Verified by phone with [name] on [date] - [signature]\'), reference request and response emails, or reference verification log showing date and method of verification.', '2025-12-15 01:45:40', NULL, 'Probationary period review documentation', 'Review records containing: performance assessment against agreed objectives, competency demonstrated in key duties, attendance and punctuality record, values and behaviours assessment, areas for development identified with support plan, decision to confirm employment or extend probation with reasons, employee and manager signatures, date of formal review meeting');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60013, 23, '23.5', 'There is satisfactory evidence of conduct in previous employment concerned with the provision of health or social care, or work with children or vulnerable adults (if applicable).', 'References, appraisal documents, or written statements from previous care employers confirming satisfactory conduct and performance. Only applicable if staff member has previous care experience.', NULL, NULL, 'Care employer reference stating \'conduct was professional and satisfactory throughout employment\', performance appraisal showing \'meets expectations\' or above, or written statement from previous care manager regarding conduct and capability.', '2025-12-15 01:45:40', NULL, 'DBS Update Service subscription or re-checking schedule', 'For Update Service: DBS certificate numbers with Update Service subscription references, quarterly online status checks recorded with dates and outcomes, consent forms from employees; For re-checking: schedule showing 3-yearly DBS renewal dates, new certificates obtained before expiry, risk assessments if any gaps in coverage');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60014, 23, '23.6', 'Evidence of appropriate qualifications (including PIN and revalidation status for registered nurses, or professional registration for social workers).', 'Copies of qualification certificates, NMC PIN verification printout showing current registration and revalidation date, Social Work England registration, or other professional body registration as applicable to role.', NULL, NULL, 'NVQ Level 2/3 in Health and Social Care certificate, NMC online register check showing active PIN and next revalidation date, Social Work England registration certificate, or degree certificate for graduate roles.', '2025-12-15 01:45:40', NULL, 'Risk assessment for staff with disclosed criminal convictions', 'Risk assessment document containing: nature and date of offence, relevance to regulated activity and vulnerable persons, time elapsed since offence, evidence of rehabilitation, specific safeguards and supervision arrangements put in place, management oversight and monitoring plan, decision rationale with senior manager approval and signature, review date set');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60015, 23, '23.7', 'For sponsored workers: There is evidence of a valid Certificate of Sponsorship (CoS) issued by the employer for this staff member\'s Health and Care Worker visa application.', 'Copy of Certificate of Sponsorship showing CoS reference number, job title, salary, start date, and confirmation it matches the staff member\'s visa endorsement.', NULL, NULL, 'Certificate of Sponsorship document from Sponsor Management System, email confirmation of CoS assignment, or CoS reference number recorded with verification that visa endorsement matches CoS details.', '2025-12-15 01:45:40', NULL, 'Certificate of Sponsorship and visa documentation', 'Certificate of Sponsorship reference number and assignment details, visa or biometric residence permit showing permission for the specific job and employer, start and end dates of permission, conditions of stay, right to work check conducted with follow-up dates set for time-limited permission');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60016, 23, '23.8', 'For sponsored workers: There is a copy of the staff member\'s current visa or Biometric Residence Permit (BRP) showing Health and Care Worker visa status, with validity dates recorded and follow-up check scheduled before expiry.', 'Copy of visa vignette in passport or BRP card showing Health and Care Worker visa category, start and end dates, and any conditions. Diary note or system reminder set for follow-up check at least 1 month before expiry.', NULL, NULL, 'BRP card copy (front and back) showing visa expiry date, passport page with visa vignette, and calendar reminder or HR system alert set for follow-up right to work check before visa expires.', '2025-12-15 01:45:40', NULL, 'Overseas criminal records certificate or explanation', 'Criminal records certificate from country of residence for 12 months or more in last 10 years, certificate translated into English if necessary, or signed statement explaining why certificate unavailable with alternative checks undertaken (character references, embassy letters), Home Office guidance followed');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60017, 23, '23.9', 'For sponsored workers: There is an overseas criminal record certificate from the staff member\'s country of residence for the 12 months prior to their UK visa application, as required for Health and Care Worker visa.', 'Criminal record certificate from country of residence immediately before UK entry, translated into English if necessary, and certified as genuine. This is a visa requirement so should be available.', NULL, NULL, 'Police clearance certificate from home country dated within 6 months of visa application, certified translation if not in English, or Home Office acknowledgement that certificate was provided as part of visa application.', '2025-12-15 01:45:40', NULL, 'TB screening test certificate', 'TB test certificate from approved clinic, test date within 6 months of UK arrival, negative result confirmed, or chest X-ray and treatment completion certificate if positive result, compliance with UK immigration health requirements demonstrated');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60018, 23, '23.10', 'For sponsored workers from TB-testing required countries: There is evidence of a TB test certificate.', 'TB test certificate from approved clinic, dated within 6 months of visa application, showing clear result. Only applicable if staff member is from a TB-testing required country (see gov.uk list).', NULL, NULL, 'TB test certificate from approved testing centre, medical examination report showing clear TB screening, or Home Office documentation confirming TB test requirement was met for visa.', '2025-12-15 01:45:41', NULL, 'English language qualification certificate', 'IELTS certificate or equivalent showing minimum B1 level (or B2 for regulated professionals), test taken at approved centre, certificate number and test date, scores in reading, writing, speaking and listening, or evidence of degree taught in English, meeting Home Office requirements for sponsored worker visa');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60019, 23, '23.11', 'For sponsored workers: There is evidence that the sponsored worker meets the English language requirement for their Health and Care Worker visa (CEFR Level B1 or equivalent).', 'Proof of English language qualification accepted by Home Office (IELTS, OET, or degree taught in English), OR evidence that staff member is from majority English-speaking country exempt from this requirement.', NULL, NULL, 'IELTS certificate showing minimum B1 in all components, Occupational English Test (OET) results, degree certificate from UK university or English-taught programme, or passport from exempt country (e.g., USA, Canada, Australia).', '2025-12-15 01:45:41', NULL, 'Sponsorship duties compliance record', 'Record showing: right to work checks conducted before employment and at visa renewal, contact details kept current, absence monitoring and reporting to Home Office if required, changes to role or salary reported, evidence of ongoing compliance with sponsor licence duties, annual review of sponsorship status');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60020, 23, '23.13', 'For staff with time-limited right to work: There is evidence that the visa has not expired, the staff member has permission for the type of work offered (including any hour restrictions), and a follow-up check is scheduled before the visa expiry date.', 'Copy of visa or BRP showing work permissions and expiry date, verification that job role and hours comply with visa conditions, and documented follow-up check date set for at least 1 month before expiry.', NULL, NULL, 'Student visa with term-time/vacation hour limits recorded and monitored, Skilled Worker visa showing permitted occupation code matches job role, or Graduate visa with expiry date and follow-up check scheduled.', '2025-12-15 01:45:41', NULL, 'Training matrix and individual training records', 'Training matrix showing all staff and mandatory training status, individual training records containing: course name and provider, completion date and certificate number, expiry/renewal due date, competency assessment post-training, supervision discussions about training needs, specialist training relevant to specific role');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60021, 23, '23.14', 'For staff with time-limited right to work: There is evidence that the employer is monitoring and complying with any visa conditions or restrictions (e.g., hour limits for students, occupation restrictions for Skilled Worker visa holders).', 'System or process for monitoring visa conditions, such as timesheet reviews for hour limits, job description alignment with visa occupation code, or regular visa status checks.', NULL, NULL, 'Timesheet monitoring log for student staff showing compliance with 20-hour term-time limit, job description matched to Skilled Worker visa occupation code, or quarterly visa status review checklist.', '2025-12-15 01:45:41', NULL, 'Mandatory training certificates and competency assessments', 'Certificates for: safeguarding adults and children (Level 2 minimum), Mental Capacity Act and Deprivation of Liberty Safeguards, infection prevention and control, manual handling, fire safety, first aid (designated first aiders), health and safety, food hygiene (if applicable), with completion dates and renewal schedules');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60022, 23, '23.15', 'There is a contract of employment which has been signed by both parties.', 'Signed contract of employment containing terms and conditions, job title, salary, working hours, and start date, with signatures from both employer and employee.', NULL, NULL, 'Signed employment contract with both parties\' signatures and dates, or electronic contract with digital signature confirmation from both parties.', '2025-12-15 01:45:41', NULL, 'Care Certificate completion evidence', 'Care Certificate portfolio containing: all 15 standards completed and signed off, workplace assessments by supervisor, reflective accounts, evidence of competency in each standard, completion certificate, enrolment within 12 weeks of starting role, completion within 12-16 weeks of employment start');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60023, 23, '23.16', 'Where there are agreed salary deductions (e.g., for accommodation, uniform, or training costs), these are recorded within a formal agreement signed and dated by employer and employee, and comply with National Minimum Wage regulations.', 'Signed salary deduction agreement specifying amount, purpose, and duration of deductions, with evidence that deductions do not reduce pay below National Minimum Wage.', NULL, NULL, 'Accommodation deduction agreement signed by both parties with calculation showing net pay remains above NMW, uniform cost recovery agreement, or training cost repayment clause in contract with NMW compliance check.', '2025-12-15 01:45:41', NULL, 'Specialist training certificates relevant to service user needs', 'Training certificates for: dementia care, learning disabilities, autism awareness, end of life care, diabetes management, catheter care, PEG feeding, mental health awareness, challenging behaviour, specific conditions relevant to service users supported, with dates and provider details');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60024, 23, '23.17', 'There is a copy of the staff member\'s job description on their file for their current role.', 'Job description specific to the staff member\'s role, outlining key responsibilities, reporting lines, and essential requirements.', NULL, NULL, 'Signed job description with staff member\'s name and start date, role-specific job description filed in personnel record, or job description acknowledged in contract or induction paperwork.', '2025-12-15 01:45:41', NULL, 'Oliver McGowan Mandatory Training on Learning Disability and Autism', 'Oliver McGowan training certificate showing: Tier 1 (all staff) or Tier 2 (health and care staff) completion, training provider accredited by NHS England, completion date, certificate number, learning outcomes achieved, compliance with statutory requirement from 2024');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60025, 23, '23.18', 'Where there is a relevant health concern that may impact on their employment, there is an appropriate risk assessment in place (e.g., pregnancy, musculoskeletal conditions, mental health, epilepsy).', 'Health declaration form completed by staff member, and where health concerns are disclosed, a risk assessment addressing how the condition may impact work and what reasonable adjustments or support are in place.', NULL, NULL, 'Completed health questionnaire, pregnancy risk assessment, manual handling risk assessment for staff with back problems, or occupational health referral and recommendations with adjustments implemented.', '2025-12-15 01:45:41', NULL, 'Training needs analysis and personal development plan', 'Document containing: skills gap analysis based on role requirements, training needs identified through supervision and appraisal, personal development goals agreed with employee, training plan with dates and providers, budget allocation, progress review dates, link to service improvement objectives');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60026, 23, '23.19', 'For agency staff: Staff profiles are in place which detail full name, photograph, qualifications and experience, DBS status and date, right to work verification, all mandatory and specialist training including renewal dates, PIN and NMC revalidation status (for nursing staff), and competency assessments.', 'Agency staff profile document or file containing all required information listed above, provided by the agency and verified by the care provider.', NULL, NULL, 'Agency worker profile pack with photo ID, DBS certificate number and date, training matrix showing mandatory training completion and renewal dates, NMC PIN verification for nurses, and competency assessment records for medication administration and moving and handling.', '2025-12-15 01:45:41', NULL, 'Agency worker profile and Schedule 3 compliance checks', 'Agency profile containing: full name and recent photograph, DBS certificate number and issue date, professional registration details and expiry date, mandatory training certificates with renewal dates, immunisation status, right to work confirmation, agency contract terms, insurance and indemnity details');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60027, 23, '23.20', 'For agency staff: There is relevant induction completed by the care provider on their first shift covering ID checks, orientation of service, fire procedure and security arrangements, roles and responsibilities, and access to appropriate records.', 'Provider-completed induction checklist signed and dated by both the agency worker and the supervising staff member, covering all required topics. Agency induction alone is not sufficient.', NULL, NULL, 'First shift induction checklist completed and signed, site-specific orientation record, fire safety briefing sign-off, or agency worker induction log showing topics covered and signatures.', '2025-12-15 01:45:41', NULL, 'Agency staff induction record for first shift', 'Induction checklist completed on first shift showing: site orientation (fire exits, alarm points, equipment locations), introduction to service users and their needs, emergency procedures explained, key policies reviewed (safeguarding, whistleblowing, infection control), supervising staff member identified, competency assessment in key tasks, signature of agency worker and supervisor');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60028, 23, '23.21', 'For agency staff: There is evidence that the care provider has verified the agency has conducted appropriate right to work and DBS checks for agency staff, and that these checks are current and valid.', 'Written confirmation from agency that right to work and DBS checks have been completed, with dates and reference numbers, OR copies of the actual checks provided by the agency.', NULL, NULL, 'Agency compliance certificate confirming checks completed, DBS certificate copy with issue date, right to work check documentation from agency, or agency contract clause requiring provision of compliance evidence with verification records.', '2025-12-15 01:45:41', NULL, 'Agency contract and service level agreement', 'Contract between provider and agency specifying: agency\'s responsibility for DBS and reference checks, professional registration verification requirements, mandatory training currency requirements, replacement arrangements if worker unsuitable, payment terms and cancellation policy, insurance and liability provisions, quality monitoring arrangements');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60029, 23, '23.22', 'For bank staff: Bank staff have undergone the same recruitment checks as permanent staff, including DBS, references, right to work, qualifications, and identity verification.', 'Bank staff personnel file containing all standard recruitment checks as required for permanent staff (DBS, two references, right to work, qualifications, identity verification, employment history).', NULL, NULL, 'Bank staff file with enhanced DBS, two verified references, right to work documentation, qualification certificates, and completed application form - same standard as permanent staff files.', '2025-12-15 01:45:41', NULL, 'Bank staff personnel file with all Schedule 3 information', 'Personnel file containing same requirements as permanent staff: enhanced DBS certificate, minimum two employment references, identity and right to work documents, professional registration certificate (if applicable), health clearance, bank contract specifying terms, induction completion record, mandatory training certificates current, availability and shift pattern preferences');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60030, 23, '23.24', 'For bank staff: Bank staff have completed a full induction and maintain up-to-date mandatory training in line with permanent staff requirements.', 'Induction checklist completed for bank staff, and training matrix showing all mandatory training current with same renewal schedules as permanent staff.', NULL, NULL, 'Bank staff induction record, training certificates for safeguarding, moving and handling, infection control, fire safety, and medication (if applicable), with renewal dates monitored.', '2025-12-15 01:45:41', NULL, 'Bank staff availability and deployment records', 'Records showing: availability submitted in advance, shift allocation process, continuity of care considered in deployment, competency matched to service user needs, maximum hours worked monitored, rest periods between shifts recorded, payment and timesheet records, performance and suitability reviews');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60031, 23, '23.25', 'There is a process for the safe recruitment of volunteers (if applicable).', 'Volunteer recruitment policy outlining checks required (proportionate to role and level of contact with service users), application process, and induction requirements.', NULL, NULL, 'Volunteer policy document, volunteer application forms, DBS checks for volunteers with direct care contact, volunteer induction checklist, or volunteer handbook outlining expectations and safeguarding.', '2025-12-15 01:45:41', NULL, 'Staff supervision records', 'Supervision notes containing: date and attendees, performance discussion against objectives, training and development needs identified, wellbeing and workload discussed, safeguarding concerns raised and actions, reflective practice encouraged, action plan agreed with timescales, next supervision date set, signatures of supervisee and supervisor, minimum monthly frequency for care staff');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60032, 23, '23.26', 'There is an effective approach to managing staff absences for all staff.', 'Absence management policy, absence recording system, and evidence of absence monitoring and return-to-work procedures.', NULL, NULL, 'Sickness absence policy, absence tracking spreadsheet or system reports, return-to-work interview forms, or absence management meeting notes for staff with concerning absence patterns.', '2025-12-15 01:45:41', NULL, 'Annual appraisal documentation', 'Appraisal record containing: review of previous year\'s objectives and achievements, performance assessment against competency framework, values and behaviours demonstrated, training completed and impact assessed, new objectives set for coming year (SMART), personal development plan updated, salary review outcome, employee and manager signatures, date of appraisal meeting');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (60033, 23, '23.27', 'There is a system in place for ongoing monitoring of staff compliance, including DBS renewals, professional registration renewals, visa expiry dates, and mandatory training renewals.', 'Compliance tracking system (spreadsheet, HR software, or diary system) showing renewal dates for all time-sensitive checks and training, with alerts set for upcoming renewals.', NULL, NULL, 'Staff compliance matrix showing DBS dates, NMC revalidation dates, visa expiry dates, and training renewal dates with RAG status, or HR system automated alerts for upcoming renewals.', '2025-12-15 01:45:41', NULL, 'Disciplinary and capability records (if applicable)', 'Records showing: nature of concern or allegation, investigation conducted with evidence gathered, disciplinary hearing notes with employee representation, decision and sanctions applied with rationale, appeal process explained, improvement plan with review dates, safeguarding referrals made if required, outcome and lessons learned, compliance with employment law and ACAS code');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90001, 24, '24.1', 'There is a whistleblowing policy in place that is up-to-date and includes contact details for raising concerns outside the organisation.', 'Whistleblowing policy document dated within last 2 years, containing clear procedures for raising concerns and external contact details (CQC, local authority safeguarding team, or whistleblowing helpline).', NULL, NULL, 'Whistleblowing policy with review date, external reporting contacts listed (CQC: 03000 616161, NSPCC Whistleblowing Helpline: 0800 028 0285), or policy document showing staff how to escalate concerns beyond management.', '2025-12-15 02:01:48', NULL, 'Safeguarding concern record and referral', 'Safeguarding record containing: date and time of concern, description of allegation or concern, category of abuse suspected (physical, emotional, sexual, financial, neglect, discriminatory), person\'s account in their own words where possible, immediate action taken to ensure safety, safeguarding referral made to local authority with date and reference number, family/representative informed (unless safeguarding risk), CQC notification if required, staff member reporting and manager notified, investigation plan, outcome and lessons learned');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90002, 24, '24.2', 'There is a bullying and harassment policy in place that references the whistleblowing policy and includes information on escalating concerns outside the organisation.', 'Bullying and harassment policy dated within last 2 years, cross-referencing whistleblowing procedures and providing external reporting routes.', NULL, NULL, 'Bullying and harassment policy with whistleblowing cross-reference, external escalation routes (ACAS, trade union, or safeguarding team), or policy showing staff how to report concerns confidentially.', '2025-12-15 02:01:48', NULL, 'Safeguarding investigation and outcome records', 'Investigation documentation showing: investigation plan with timescales, evidence gathered (statements, records, photos), interviews conducted with person, alleged perpetrator, witnesses, chronology of events, analysis of evidence, safeguarding decision (substantiated, unsubstantiated, inconclusive), protection plan if needed, disciplinary action if staff involved, police involvement if criminal matter, local authority safeguarding outcome, learning and service improvements, case closure with rationale');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90003, 24, '24.3', 'There is evidence that all staff have access to core policies and procedures and there is confirmation that they have read and understood them.', 'Policy acknowledgement forms signed by staff, induction records showing policy review, or electronic system logs showing staff have accessed and confirmed understanding of key policies.', NULL, NULL, 'Signed policy acknowledgement sheets, induction checklist with policies reviewed section completed, or staff handbook receipt form with confirmation staff have read safeguarding, whistleblowing, health & safety, and equality policies.', '2025-12-15 02:01:48', NULL, 'Safeguarding protection plan', 'Protection plan containing: identified risks to person, actions to reduce risk and keep person safe, restrictions on alleged perpetrator (suspension, supervision, moved), increased monitoring or observations, changes to care plan, involvement of advocates or family, person\'s views and wishes, multi-agency involvement (police, social worker, safeguarding team), review dates, responsibility for actions, person\'s agreement to plan (or best interests if lacking capacity), plan effectiveness monitored');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90004, 24, '24.4', 'There is a policy review schedule in place ensuring all policies are reviewed at appropriate intervals (typically annually or biennially).', 'Policy register or schedule showing policy names, last review dates, next review dates, and responsible persons for each policy.', NULL, NULL, 'Policy review log or register, governance meeting minutes showing policy review discussions, or policy front pages showing version control and review dates.', '2025-12-15 02:01:48', NULL, 'Deprivation of Liberty Safeguards (DoLS) authorisation', 'DoLS documentation containing: DoLS application to supervisory body with date, assessment that person lacks capacity to consent to care arrangements, assessment that care amounts to deprivation of liberty (acid test: person under continuous supervision and control, not free to leave), assessment that DoLS is necessary and proportionate, best interests assessment, mental health assessment, age assessment, eligibility assessment, standard or urgent authorisation granted with reference number, conditions attached, authorisation start and end dates, representative appointed, review dates, person\'s rights explained');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90005, 24, '24.5', 'There is an up-to-date safeguarding policy that reflects current legislation and local safeguarding procedures.', 'Safeguarding policy dated within last 12 months, referencing Care Act 2014, local safeguarding adult board procedures, and containing local authority safeguarding contact details.', NULL, NULL, 'Safeguarding policy with local authority safeguarding team contact details, reference to local multi-agency safeguarding procedures, or policy aligned with local safeguarding adult board guidance.', '2025-12-15 02:01:48', NULL, 'Restraint records and reduction plan', 'Restraint documentation showing: each incident of restraint recorded (date, time, duration, type, staff involved), trigger or antecedent, description of restraint used (physical hold, mechanical, chemical), least restrictive option used, person\'s distress level, de-escalation attempted first, post-incident debrief and welfare check, injuries or complaints, restraint reduction plan with positive behaviour support, analysis of patterns and trends, staff training in de-escalation, DoLS consideration if restraint frequent, safeguarding referral if restraint inappropriate');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90006, 24, '24.6', 'There is an up-to-date health and safety policy that complies with the Health and Safety at Work Act 1974 and includes risk assessment procedures.', 'Health and safety policy dated within last 12 months, signed by responsible person, outlining employer and employee responsibilities and risk assessment processes.', NULL, NULL, 'Health and safety policy statement signed by manager or director, risk assessment policy and templates, or health and safety responsibilities document showing organisational structure.', '2025-12-15 02:01:49', NULL, 'Equality, diversity and inclusion policy', 'Policy containing: commitment to Equality Act 2010 protected characteristics, zero tolerance of discrimination and harassment, reasonable adjustments for staff and service users, cultural competence and awareness training, accessible information standard compliance, diverse recruitment practices, equality impact assessments, staff and service user consultation, complaints procedure, monitoring and reporting of equality data, senior management responsibility, annual review');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90007, 24, '24.7', 'There is an up-to-date infection prevention and control policy that reflects current guidance (including COVID-19 and other infectious diseases).', 'Infection prevention and control policy dated within last 12 months, reflecting current Public Health England or UK Health Security Agency guidance on infection control practices.', NULL, NULL, 'Infection control policy with PPE guidance, hand hygiene protocols, outbreak management procedures, or policy reflecting current national infection control guidance.', '2025-12-15 02:01:49', NULL, 'Confidentiality and data protection policy', 'Policy containing: UK GDPR and Data Protection Act 2018 compliance, lawful basis for processing personal data, information sharing protocols and consent requirements, data security measures (encryption, password protection, secure storage), access controls and need-to-know principle, retention and disposal schedules, data breach reporting procedures, subject access request process, staff training on confidentiality, Information Commissioner\'s Office registration, Data Protection Officer details, review date');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90008, 24, '24.8', 'There is an up-to-date medication policy that covers ordering, storage, administration, recording, and disposal of medicines.', 'Medication policy dated within last 2 years, covering all aspects of the medication cycle from ordering to disposal, including covert administration and self-administration where applicable.', NULL, NULL, 'Medication policy covering MAR charts, controlled drugs procedures, medication errors reporting, covert administration (with mental capacity assessment), or self-administration risk assessment procedures.', '2025-12-15 02:01:49', NULL, 'Complaints policy and procedure', 'Policy containing: how to make a complaint (verbal, written, online), complaints officer identified, acknowledgement timescale (e.g. 3 working days), investigation process and timescales, independent advocacy support offered, resolution and response timescale, escalation to senior management, referral to Local Government and Social Care Ombudsman, learning from complaints and service improvement, annual complaints report, accessible formats available, display of policy in service');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90009, 25, '25.1', 'There is evidence that the staff handbook has been issued and/or is available to all staff.', 'Staff handbook receipt forms signed by staff, or induction records showing staff handbook issued and reviewed.', NULL, NULL, 'Signed staff handbook receipt form, induction checklist with \'staff handbook issued\' section completed, or staff file containing copy of handbook with acknowledgement signature.', '2025-12-15 02:01:49', NULL, 'Medication Administration Record (MAR) chart', 'MAR chart containing: person\'s name, photo, allergies prominently displayed, each medication listed (name, strength, dose, route, frequency, time), prescriber\'s signature, start and review dates, administration recorded with initials, date and time, codes for non-administration explained (refused, away, nil by mouth), handwritten entries countersigned, no gaps or unexplained omissions, PRN medications with indication for use, variable dose medications with range, MAR chart audit trail, monthly MAR chart reviewed and rewritten');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90010, 25, '25.2', 'There is evidence of a comprehensive and role-specific service induction that has been signed and dated by both parties.', 'Induction checklist or programme specific to the staff member\'s role, completed and signed by both the new staff member and their supervisor/manager, with dates recorded.', NULL, NULL, 'Completed induction checklist signed by staff and manager, role-specific induction plan showing topics covered (fire safety, safeguarding, policies, care practices), or induction schedule with sign-off dates.', '2025-12-15 02:01:49', NULL, 'Medication care plan and support', 'Medication care plan containing: person\'s understanding and involvement in medication, level of support required (full administration, prompting, self-administration), self-administration risk assessment if applicable, how person prefers to take medication, best time and method, medication side effects to monitor, person\'s capacity to consent to medication, covert medication protocol if applicable with best interests decision, pharmacy involvement, GP medication reviews (minimum annually), medication changes documented with reason');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90011, 25, '25.3', 'There is evidence of staff shadowing at induction, with records showing who was shadowed, dates, and areas covered.', 'Shadowing log or induction record showing dates of shadowing shifts, names of experienced staff shadowed, and areas of practice observed (personal care, medication, mealtimes, activities).', NULL, NULL, 'Shadowing record with dates and signatures, induction checklist with shadowing section completed showing shifts worked alongside experienced staff, or supervision notes discussing shadowing experience.', '2025-12-15 02:01:49', NULL, 'PRN (as required) medication protocol', 'PRN protocol for each medication containing: medication name and dose, specific indication for use (e.g. \'for anxiety when...\', \'for pain when...\'), signs and symptoms requiring PRN, non-pharmacological interventions tried first, maximum dose in 24 hours, minimum time between doses, monitoring after administration, effectiveness recorded, when to seek medical advice, person\'s views on PRN use, review of PRN usage patterns to identify unmet needs');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90012, 25, '25.4', 'There is evidence that the Care Certificate has been completed within the first 12 weeks for new care and support staff entering care (including evidence of workbook having been signed and agreed by both parties), OR evidence that staff hold equivalent qualifications.', 'Completed Care Certificate workbook signed by staff and assessor with completion date within 12 weeks of start date, OR evidence of equivalent qualification (NVQ Level 2 in Health & Social Care or higher) accepted in lieu of Care Certificate.', NULL, NULL, 'Care Certificate portfolio with all 15 standards completed and signed within 12 weeks, Care Certificate completion certificate, or NVQ Level 2/3 in Health & Social Care certificate accepted as equivalent qualification.', '2025-12-15 02:01:49', NULL, 'Controlled drugs register and records', 'CD register showing: separate page for each controlled drug, running balance maintained, each administration recorded (date, time, dose, person\'s name, prescriber, administrator signature, witness signature), balance checked and signed by two staff, stock reconciliation (weekly minimum), discrepancies investigated and recorded, disposal witnessed and recorded, CD cupboard keys held securely, access restricted to trained staff, register audit trail, compliance with Misuse of Drugs regulations');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90013, 25, '25.5', 'There is evidence of increased supervision and support during the induction period (probationary period), with regular check-ins documented.', 'Supervision records showing more frequent meetings during probation (e.g., weekly or fortnightly), induction review meetings at 4, 8, and 12 weeks, or probation review documentation.', NULL, NULL, 'Probation supervision notes at regular intervals, induction review meetings documented, or probation assessment form completed at end of probationary period with recommendation for confirmation of employment.', '2025-12-15 02:01:49', NULL, 'Medication errors and incident reports', 'Incident report containing: date and time of error, medication involved, nature of error (omission, wrong dose, wrong person, wrong time, wrong route), how error discovered, immediate action taken, person\'s condition monitored, GP/pharmacist informed if required, family informed, safeguarding consideration, root cause analysis, learning and actions to prevent recurrence, staff support and retraining, error trends analysed, reporting to CQC if serious');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90014, 25, '25.6', 'There is evidence that new staff have been assessed as competent in key areas before working unsupervised (e.g., moving and handling, medication administration, safeguarding recognition).', 'Competency assessment records signed by qualified assessor confirming staff member is competent to work unsupervised in specific areas, with dates recorded.', NULL, NULL, 'Moving and handling competency sign-off, medication administration competency assessment, or induction competency checklist showing staff assessed as competent in key tasks before working alone.', '2025-12-15 02:01:49', NULL, 'Covert medication best interests decision', 'Covert medication documentation containing: capacity assessment showing person lacks capacity to consent to medication, best interests meeting held with GP, pharmacist, family, care staff, decision that covert administration in person\'s best interests, less restrictive options considered, how medication will be disguised, monitoring for effectiveness and side effects, pharmacy advice on safe administration, regular review of continued need, person\'s previous wishes considered, DoLS consideration, documentation of each covert administration');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90015, 25, '25.7', 'There is evidence that new staff are asked for feedback on their induction experience, and this feedback is used to improve the induction process.', 'Induction feedback forms completed by new staff, or end-of-induction review meetings where staff feedback is recorded and acted upon.', NULL, NULL, 'Induction evaluation form completed by new staff, probation review meeting notes including staff feedback on induction, or induction improvement log showing changes made based on staff feedback.', '2025-12-15 02:01:49', NULL, 'Induction evaluation and feedback', 'Evaluation form completed by new employee containing: quality of induction programme rated, usefulness of shadowing and mentoring, clarity of role expectations, confidence in key tasks, areas where more support needed, suggestions for improvement, overall satisfaction score, feedback used to improve induction for future staff, evaluation reviewed by management');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90016, 25, '25.8', 'There is a process for re-induction or refresher induction for staff returning after extended absence (e.g., maternity leave, long-term sickness) or moving to a new role.', 'Re-induction checklist or return-to-work induction plan for staff returning after extended leave, covering policy updates, changes in practice, and refresher training.', NULL, NULL, 'Return-to-work induction checklist, re-induction plan for staff returning after 6+ months absence, or role change induction for internal promotions or transfers.', '2025-12-15 02:01:49', NULL, 'Induction for bank and agency staff', 'Abbreviated induction checklist for bank/agency staff containing: site-specific orientation (layout, fire exits, equipment), introduction to current service users and their care needs, emergency procedures and contacts, key policies (safeguarding, whistleblowing, infection control), location of care plans and records, supervising staff member identified, shift-specific expectations, competency check in key tasks, completed on first shift before independent practice, signature of worker and supervisor');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90017, 26, '26.1', 'There is evidence of a minimum of 6 recorded supervisions per annum (50% of which can be completed in a group setting/team meeting), that are signed and dated and cover all aspects of care practice, philosophy of care, career development needs, practical aspects of the job role, and adhering to the Skills for Care Code of Conduct.', 'Supervision matrix or log showing all staff have received at least 6 supervisions per year, with supervision records signed by both parties and covering required topics.', NULL, NULL, 'Supervision matrix showing 6+ sessions per staff member per year, signed supervision notes covering care practice, development, and conduct, or team meeting minutes counted towards group supervision (maximum 3 of 6 sessions).', '2025-12-15 02:01:49', NULL, 'Current menu showing variety of meal options, dietary choices (vegetarian, cultural, religious), and evidence of menu planning process involving service users.', 'Weekly menu displaying: breakfast options (cereals, cooked breakfast, continental), lunch choices (meat, fish, vegetarian), dinner selections, cultural meal options (halal, kosher), seasonal variations, and evidence of service user consultation in menu planning (feedback forms, residents\' meetings minutes).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90018, 26, '26.2', 'There is evidence of clinical supervisions for nursing staff that are signed and dated and cover the required clinical areas. All nursing staff have received a minimum of 6 recorded clinical supervisions per annum (50% of which can be completed in a clinical team meeting). Clinical supervision should be completed by a skilled clinical facilitator and cover: reflective component, focus on clinical practice including team dynamics and communication, professional development (PIN and revalidation) including CPD.', 'Clinical supervision records for all nursing staff showing 6+ sessions per year, facilitated by qualified clinical supervisor, covering reflection, clinical practice, and NMC revalidation requirements.', NULL, NULL, 'Clinical supervision notes signed by clinical facilitator and nurse, reflective practice discussions documented, NMC revalidation progress reviewed in supervision, or clinical team meeting minutes counted towards group clinical supervision.', '2025-12-15 02:01:49', NULL, 'Policy or procedure document confirming 24-hour access to food and fluids, kitchen accessibility arrangements, and staff training records on food preparation.', 'Nutrition policy stating: kitchen accessible 24/7 or secure food storage in communal areas, staff trained in food hygiene and preparation, availability of snacks/drinks outside meal times, night staff access to facilities for preparing hot drinks and light meals.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90019, 26, '26.3', 'Supervision records demonstrate meaningful, two-way discussions that support staff wellbeing, development, and performance improvement (not just task-focused checklists).', 'Supervision notes showing discussion of staff wellbeing, reflections on practice, development goals, and actions agreed by both parties - evidence of dialogue rather than one-way instruction.', NULL, NULL, 'Supervision notes with staff reflections recorded, wellbeing check-ins documented, development goals set collaboratively, or evidence that staff contribute to supervision agenda and discussions.', '2025-12-15 02:01:49', NULL, 'Individual nutritional support plans containing: known allergies, food preferences, dislikes, cultural/religious dietary requirements, and IDDSI (International Dysphagia Diet Standardisation Initiative) levels where applicable.', 'Nutritional care plan documenting: \"No known allergies\" or specific allergies listed (nuts, dairy, gluten), food preferences (likes fish, dislikes mushrooms), cultural needs (halal meat, no pork), IDDSI level if required (Level 4 pureed, Level 1 thin liquids), portion size preferences.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90020, 26, '26.4', 'There is evidence that all staff receive an annual appraisal that reviews performance, sets objectives for the coming year, and identifies development needs.', 'Annual appraisal records for all staff, signed by both parties, reviewing previous year\'s objectives and setting new goals aligned with role and organisational objectives.', NULL, NULL, 'Completed annual appraisal forms signed and dated, appraisal schedule showing all staff appraised within last 12 months, or appraisal documentation with SMART objectives set for next year.', '2025-12-15 02:01:49', NULL, 'Nutritional support plans incorporating professional dietary advice from: dietician assessments, Speech and Language Therapy (SALT) recommendations, or other specialist input, with dates of assessments and review dates.', 'Care plan section containing: dietician report dated within last 6 months with specific dietary recommendations, SALT assessment detailing swallowing difficulties and safe food/fluid consistencies, implementation plan showing how professional advice is followed in daily practice.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90021, 26, '26.5', 'There is evidence that appraisal targets are reviewed throughout the year (during supervision/performance management) and records are signed and dated by both parties.', 'Supervision notes showing review of appraisal objectives and progress towards goals, with updates recorded and signed by both staff member and supervisor.', NULL, NULL, 'Supervision records referencing appraisal objectives, progress reviews documented in supervision notes, or mid-year appraisal review meetings held to assess progress towards annual goals.', '2025-12-15 02:01:49', NULL, 'Nutritional support plans documenting MUST (Malnutrition Universal Screening Tool) score, risk category (low/medium/high), and specific actions to address identified nutritional risks.', 'MUST assessment showing: calculated score (0=low risk, 1=medium risk, 2+=high risk), risk category clearly stated, action plan detailing: food fortification, supplement drinks, increased meal frequency, referral to dietician, weight monitoring schedule.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90022, 26, '26.6', 'There is a clear and appropriate process for supporting and managing staff regarding performance which does not meet the service and organisation\'s agreed standards.', 'Performance management policy outlining stages of support (informal discussion, formal performance improvement plan, disciplinary process), or evidence that managers can explain the performance management process.', NULL, NULL, 'Performance management policy, capability procedure document, or evidence of performance improvement plans used with clear targets, support offered, and review dates set.', '2025-12-15 02:01:49', NULL, 'Completed MUST assessment tools for each service user requiring nutritional monitoring, showing: calculation workings, BMI, weight loss percentage, acute disease effect score, and evidence of monthly reviews or reviews following change in condition.', 'MUST form containing: current weight and height measurements, BMI calculation, percentage weight loss over 3-6 months, acute disease score if applicable, total MUST score, date completed, staff signature, evidence of monthly review dates or re-assessment following illness/hospital admission.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90023, 26, '26.7', 'There is evidence of up-to-date, robust role-specific competency assessments in place and that staff have been assessed by a suitably qualified person (examples may include moving and handling, specialist nursing procedures, medication administration, use of specialist equipment).', 'Competency assessment records for all staff in role-specific areas, completed at least annually or after training, signed by qualified assessor confirming competence.', NULL, NULL, 'Moving and handling competency assessment signed by trainer, medication administration competency signed by senior nurse or manager, specialist equipment competency (e.g., hoist, syringe driver) assessed by qualified person, or annual competency review schedule.', '2025-12-15 02:01:49', NULL, 'Displayed menu that is current (showing correct week/date), clearly visible to service users and visitors, and accurately reflects meals being served.', 'Menu board or printed menu displayed in communal dining area showing: current week dates, daily meal options matching what is actually served, special dietary options clearly marked, updated regularly to reflect seasonal changes or special occasions.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90024, 26, '26.8', 'There is evidence of documented direct observations of care practice undertaken including spot checks, covering areas such as personal care, medication administration, infection control, and communication with service users.', 'Observation records or spot check forms completed by managers or senior staff, documenting observations of care practice with feedback given to staff.', NULL, NULL, 'Spot check forms for medication administration, personal care observation records, infection control practice observations, or unannounced observations of care practice with feedback recorded.', '2025-12-15 02:01:49', NULL, 'Service user feedback records, meeting minutes, or care plan notes evidencing that individual food and drink requests are accommodated.', 'Residents\' meeting minutes recording discussion of menu preferences, care plan notes stating individual requests (\"prefers Earl Grey tea\", \"likes decaf coffee after 2pm\"), kitchen records showing stock of specialist items, feedback forms showing requests have been actioned.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90025, 26, '26.9', 'There is evidence that staff who provide supervision to others (e.g., senior care staff, team leaders) receive supervision themselves and have received training in providing effective supervision.', 'Supervision records for supervisors showing they receive regular supervision, and evidence of supervision skills training or competency in providing supervision.', NULL, NULL, 'Supervision records for team leaders and senior staff, supervision skills training certificates, or evidence that registered manager supervises those who supervise others.', '2025-12-15 02:01:49', NULL, 'Observation records or quality monitoring notes documenting mealtime experience including: dining environment, staff interaction, support provided, use of adaptive equipment, and service user engagement.', 'Mealtime observation form noting: calm and unhurried atmosphere, appropriate music/noise levels, staff sitting with service users during meals, use of adapted cutlery/crockery where needed, show plates used for choice, table settings appropriate, staff promoting independence whilst offering assistance as needed.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90026, 27, '27.1', 'There is evidence that a training matrix or equivalent is in place which documents all training undertaken and highlights renewal dates.', 'Training matrix or database showing all staff training records, including course names, completion dates, expiry dates, and renewal due dates.', NULL, NULL, 'Training matrix spreadsheet with all staff and training courses, training database printout showing compliance status, or training tracker with RAG rating for renewals due.', '2025-12-15 02:01:49', NULL, 'Individual activity plans showing: person\'s interests, hobbies, preferred activities, frequency of participation, and evidence of regular review.', 'Activity care plan documenting: life history (former occupation, hobbies, interests), current activity preferences (gardening, music, reading), planned activities schedule (bingo Tuesdays, exercise class Thursdays), one-to-one activity time, community access plans, review dates showing quarterly updates.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90027, 27, '27.2', 'There is evidence that all staff have undertaken appropriate mandatory and statutory training, including: safeguarding adults, health and safety, fire safety, infection prevention and control, moving and handling, first aid, food hygiene (if applicable), medication administration (if applicable), and basic life support.', 'Training records showing all staff have completed core mandatory training within required timeframes (typically annually or every 3 years depending on topic), with certificates or training logs as evidence.', NULL, NULL, 'Training certificates for mandatory topics, training matrix showing 100% compliance for mandatory training, or e-learning completion records for statutory training courses.', '2025-12-15 02:01:49', NULL, 'Activity programme or schedule showing variety of activities offered, including: group activities, one-to-one sessions, community access, and activities suitable for different ability levels.', 'Monthly activity calendar displaying: daily group activities (arts/crafts, quizzes, music sessions), weekly outings (garden centre, caf visits), one-to-one time slots, activities for varying abilities (seated exercise, reminiscence, sensory activities), evening/weekend activities, special events.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90028, 27, '27.3', 'There is evidence that all staff have completed the Oliver McGowan Mandatory Training on Learning Disability and Autism at the appropriate tier (Tier 1 for all staff, Tier 2 for staff with regular contact with people with learning disabilities or autism).', 'Training certificates or completion records showing staff have completed Oliver McGowan training at Tier 1 (all staff) and Tier 2 (relevant staff), as this is now a statutory requirement for all health and social care staff.', NULL, NULL, 'Oliver McGowan Tier 1 training certificates for all staff, Tier 2 certificates for care staff and managers, or e-learning completion records from approved Oliver McGowan training provider.', '2025-12-15 02:01:49', NULL, 'Activity participation records showing: which service users attended activities, level of engagement, enjoyment observed, and any barriers to participation addressed.', 'Activity attendance log recording: date, activity type, names of participants, engagement level (actively participated/observed/declined), notes on enjoyment (\"smiled throughout\", \"requested to do again\"), reasons for non-participation (unwell, preferred to rest), follow-up actions.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90029, 27, '27.4', 'There is evidence that all staff have undertaken any additional training required to meet the specific needs of the individuals supported (e.g., epilepsy, diabetes, Parkinson\'s, multiple sclerosis, dementia, mental health, physical disabilities, learning disabilities) and in line with CQC registration, which is more than basic awareness.', 'Training records showing staff have completed condition-specific training relevant to the needs of service users, going beyond basic awareness to practical skills and knowledge.', NULL, NULL, 'Dementia care training certificates (beyond awareness level), diabetes management training for staff supporting diabetic service users, epilepsy awareness and seizure management training, or mental health training for staff in mental health services.', '2025-12-15 02:01:49', NULL, 'Service user feedback on activities through: verbal feedback records, satisfaction surveys, residents\' meeting minutes, or care plan reviews showing activities are meaningful and enjoyable.', 'Feedback documentation showing: residents\' meeting minutes discussing activity preferences, satisfaction survey results on activity programme, care plan review notes recording service user comments (\"enjoys gardening club\", \"would like more music sessions\"), evidence of changes made based on feedback.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90030, 27, '27.5', 'There is evidence of blended learning approaches (e.g., classroom, face-to-face, e-learning, practical skills sessions, reflective practice) to ensure effective learning and skill development.', 'Training records showing variety of learning methods used, including face-to-face training, e-learning courses, practical skills sessions, and reflective practice discussions.', NULL, NULL, 'Mix of e-learning certificates and face-to-face training attendance records, practical skills training with competency assessments, or reflective practice sessions documented in supervision.', '2025-12-15 02:01:49', NULL, 'Training programme documentation showing variety of learning methods used including: classroom sessions, e-learning modules, practical skills training, face-to-face coaching, and reflective practice opportunities.', 'Training records showing: mix of delivery methods (classroom training dates, e-learning completion certificates, practical skills sessions with competency sign-off, shadowing/mentoring records, reflective practice sessions in supervision), learning styles accommodated, blended approach ensuring knowledge retention and skill application, accessibility considerations for different learning needs.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90031, 27, '27.6', 'There is evidence of a training needs analysis process that identifies individual and organisational training needs based on service user needs, staff roles, and regulatory requirements.', 'Training needs analysis document or process showing how training needs are identified from appraisals, supervision, service user needs assessments, and regulatory changes.', NULL, NULL, 'Annual training needs analysis report, training plan based on identified needs, or appraisal forms with training needs section completed and fed into training planning.', '2025-12-15 02:01:49', NULL, 'Training needs analysis process documentation showing: individual staff training needs identified, organisational training priorities, service user needs driving training requirements, and regulatory compliance training identified.', 'Training needs analysis documentation containing: annual training needs analysis process, individual staff development plans identifying personal training needs, organisational training priorities based on service user population (dementia care, diabetes management, end of life care), regulatory requirements mapped (CQC fundamental standards, mandatory training), supervision discussions identifying training needs, incident/audit findings driving training requirements.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90032, 27, '27.7', 'There is evidence that training effectiveness is evaluated (e.g., through post-training assessments, competency checks, observations of practice, or feedback from staff and service users).', 'Training evaluation forms, post-training competency assessments, or evidence that learning from training is applied in practice and monitored.', NULL, NULL, 'Training evaluation forms completed by staff, post-training competency assessments showing knowledge gained, or supervision notes discussing application of training in practice.', '2025-12-15 02:01:49', NULL, 'Training effectiveness evaluation records showing: post-training assessments, competency checks, observations of practice applying learning, and feedback from staff and service users on training impact.', 'Training evaluation evidence showing: post-training knowledge tests/quizzes, competency assessments (observed practice applying training), supervision discussions reviewing application of learning, care observations showing improved practice, staff feedback on training usefulness, service user/family feedback on care improvements, re-audit showing improvements following training, training impact measured (reduced incidents, improved audit scores).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90033, 27, '27.8', 'There is evidence that staff are supported to access professional development opportunities beyond mandatory training (e.g., NVQ/diploma qualifications, leadership development, specialist courses).', 'Records of staff undertaking vocational qualifications, leadership training, or specialist courses, with evidence of organisational support (time off, funding, mentoring).', NULL, NULL, 'Staff enrolled on NVQ Level 2/3/5 in Health & Social Care, leadership and management training for senior staff, or specialist qualification support (e.g., end of life care, mental health).', '2025-12-15 02:01:49', NULL, 'Professional development records showing: staff accessing qualifications beyond mandatory training, support provided for career development, specialist training opportunities, and leadership development programmes.', 'Professional development evidence showing: staff enrolled on NVQ/diploma qualifications (Level 2, 3, 5 in Health and Social Care), leadership and management development programmes, specialist training (dementia champion, end of life care, mental health first aid), apprenticeship programmes, study leave granted, financial support for qualifications, career progression pathways, continuing professional development for registered professionals.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90034, 27, '27.9', 'Staff confirm that they have received an induction which was appropriate to their role and prepared them for their responsibilities.', 'Staff feedback forms, induction evaluation surveys, or documented discussions in supervision where staff confirm their induction was adequate and role-appropriate.', NULL, NULL, 'Induction feedback forms with positive responses, end-of-probation reviews where staff confirm induction adequacy, or staff survey results showing satisfaction with induction process.', '2025-12-15 02:01:49', NULL, 'Staff feedback on induction quality through: induction evaluation forms, probationary reviews, supervision discussions, and staff survey responses on induction preparedness.', 'Induction feedback documentation showing: staff induction evaluation forms completed (rating induction quality, preparedness for role), probationary review discussions about induction effectiveness, supervision notes recording staff confidence following induction, staff survey responses on induction experience, new starter feedback (\"induction prepared me well\", \"felt supported during induction\"), evidence of induction improvements based on feedback.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90035, 28, '28.1', 'Staff confirm they receive regular, two-way, meaningful and supportive supervisions.', 'Staff feedback from surveys, supervision evaluations, or documented discussions where staff confirm supervision frequency and quality meet their needs.', NULL, NULL, 'Staff survey results on supervision satisfaction, supervision feedback forms completed by staff, or documented discussions in team meetings about supervision quality.', '2025-12-15 02:01:49', NULL, 'Individual health action plans or care plans documenting: known health conditions, symptoms to monitor, healthcare professional involvement, and actions to maintain health and wellbeing.', 'Health section of care plan containing: diagnosed conditions (diabetes, COPD, dementia), monitoring requirements (blood sugar checks, oxygen saturations), healthcare professional input (GP, district nurse, specialist consultants), preventative measures, early warning signs of deterioration.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90036, 28, '28.2', 'Staff confirm they feel valued, supported, and listened to by management.', 'Staff survey results, exit interview feedback, or documented evidence from staff meetings showing staff feel valued and supported.', NULL, NULL, 'Annual staff satisfaction survey results, staff meeting minutes showing staff voice is heard, or positive feedback from staff interviews or focus groups.', '2025-12-15 02:01:49', NULL, 'Healthcare professional visit records showing: GP reviews, district nurse visits, specialist consultations, dates of visits, advice given, and actions taken.', 'Healthcare visit log documenting: GP annual review date, district nurse wound care visits (dates and treatment provided), chiropodist appointments, optician visits, dentist check-ups, physiotherapy sessions, record of advice given and how implemented in care plan.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90037, 28, '28.3', 'There is evidence of regular staff meetings (team meetings, handovers, or briefings) that facilitate communication, information sharing, and staff input into service development.', 'Staff meeting minutes showing regular meetings held (at least monthly), with attendance records, agenda items, and actions arising documented.', NULL, NULL, 'Team meeting minutes with dates and attendees, handover records showing information sharing, or staff briefing notes circulated to all staff.', '2025-12-15 02:01:49', NULL, 'Care plans containing specific guidance on managing health conditions, including: monitoring requirements, signs of deterioration, escalation procedures, and preventative care measures.', 'Condition-specific care plan detailing: diabetes management (blood sugar monitoring frequency, target ranges, hypo/hyper symptoms, treatment protocol), COPD management (inhaler regime, breathlessness management, when to seek medical help), dementia care (communication strategies, behaviour triggers, calming techniques).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90038, 28, '28.4', 'There is evidence that staff are consulted on and involved in service development, policy changes, and decision-making processes.', 'Consultation records, staff feedback on proposed changes, or evidence that staff suggestions have been implemented.', NULL, NULL, 'Staff consultation meeting notes, policy review feedback from staff, staff suggestion scheme with evidence of ideas implemented, or staff representation on quality improvement groups.', '2025-12-15 02:01:49', NULL, 'Records of health monitoring including: vital signs observations (blood pressure, temperature, pulse, respirations), weight monitoring, fluid balance charts, food intake records, where clinically indicated.', 'Monitoring charts showing: weekly weight records with trend analysis, daily fluid balance charts for at-risk individuals, food intake monitoring for nutritional concerns, vital signs observations with normal ranges indicated, dates and staff signatures, evidence of escalation when outside normal parameters.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90039, 28, '28.5', 'There is evidence of regular staff surveys or feedback mechanisms, with results analysed and action plans developed to address concerns or suggestions.', 'Staff survey results from last 12 months, analysis of findings, and action plan showing how feedback has been addressed.', NULL, NULL, 'Annual staff survey report with results and action plan, staff feedback analysis with themes identified, or evidence of improvements made in response to staff feedback.', '2025-12-15 02:01:49', NULL, 'Continence assessment and management plans documenting: continence status, type of products used, toileting routine, skin integrity checks, and dignity considerations.', 'Continence care plan containing: continence assessment outcome, product type and size, toileting schedule (prompted every 2 hours), night-time routine, skin care regime, dignity measures (door closed, appropriate language), review dates, evidence of specialist continence nurse involvement if needed.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90040, 28, '28.6', 'There is a process for conducting exit interviews with departing staff, and feedback is used to identify retention issues and improve working conditions.', 'Exit interview records, analysis of reasons for leaving, and evidence that exit interview feedback informs retention strategies.', NULL, NULL, 'Exit interview forms completed for leavers, exit interview analysis report identifying themes, or evidence of changes made based on exit interview feedback (e.g., improved induction, better supervision).', '2025-12-15 02:01:49', NULL, 'Tissue viability assessments and care plans for service users at risk of or with pressure damage, including: Waterlow score, repositioning schedule, pressure-relieving equipment, skin inspection routine.', 'Pressure care plan documenting: Waterlow score calculation, risk level (low/medium/high/very high), repositioning schedule (2-hourly turns, position chart), pressure-relieving equipment in use (air mattress, cushions), skin inspection frequency, body map showing any existing pressure areas, wound care regime if applicable.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90041, 28, '28.7', 'There is evidence of staff recognition and appreciation initiatives (e.g., employee of the month, thank you notes, celebration of achievements, long service awards).', 'Records of staff recognition schemes, thank you cards or messages to staff, celebration of staff achievements in meetings or newsletters.', NULL, NULL, 'Employee of the month certificates, thank you notes to staff in personnel files, long service awards records, or staff achievements celebrated in team meetings or newsletters.', '2025-12-15 02:01:49', NULL, 'Falls risk assessments and falls prevention care plans showing: falls risk score, identified risk factors, preventative measures, post-falls protocol, and equipment provided.', 'Falls care plan containing: falls risk assessment score, risk factors identified (poor mobility, postural hypotension, medication side effects), prevention strategies (walking aid provided, non-slip footwear, adequate lighting, call bell within reach), post-falls procedure, lying and standing blood pressure if indicated.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90042, 29, '29.1', 'There is evidence that the organisation actively supports staff wellbeing, including access to wellbeing resources, mental health support, or employee assistance programmes.', 'Information about wellbeing support available to staff, employee assistance programme details, or records of wellbeing initiatives offered.', NULL, NULL, 'Employee assistance programme information provided to staff, wellbeing resources available (e.g., mental health first aiders, counselling access), or staff wellbeing policy.', '2025-12-15 02:01:49', NULL, 'Up-to-date medication policy covering: ordering, receipt, storage, administration, recording, disposal, and staff competency requirements, reviewed within last 12-24 months.', 'Medication policy document containing: policy review date within last 2 years, sections covering entire medication cycle (ordering from pharmacy, checking deliveries, storage temperatures, administration procedures, recording requirements, returns and disposal), staff competency assessment requirements, audit schedule, version control.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90043, 29, '29.2', 'There is evidence of stress risk assessments for staff, particularly those in high-pressure roles or experiencing work-related stress.', 'Stress risk assessment forms completed for staff reporting stress, or organisational stress risk assessment identifying stressors and control measures.', NULL, NULL, 'Individual stress risk assessments for staff on sickness absence due to stress, organisational stress risk assessment using HSE Management Standards, or wellbeing action plans for staff experiencing stress.', '2025-12-15 02:01:49', NULL, 'Medication Administration Records (MARs) showing: legible entries, all administrations signed, gaps explained with codes, allergies recorded, photographs present, and regular audits completed.', 'MAR chart containing: service user photograph, known allergies clearly recorded, medication details (name, strength, dose, route, frequency), administration signatures in all boxes or explanation codes for gaps (R=refused, H=hospital, NH=not required), running balance for controlled drugs, monthly audit signature.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90044, 29, '29.3', 'There is evidence that the organisation promotes work-life balance through flexible working arrangements, reasonable working hours, and adequate rest breaks.', 'Flexible working policy, evidence of flexible working requests granted, or staff rotas showing reasonable working hours and adequate breaks.', NULL, NULL, 'Flexible working policy and requests granted, rotas showing compliance with Working Time Regulations (48-hour week, rest breaks), or evidence of part-time and flexible working arrangements.', '2025-12-15 02:01:49', NULL, 'Medication storage arrangements showing: locked storage (cabinet/trolley/room), temperature monitoring records, controlled drugs register, and separation of internal/external medications.', 'Medication storage meeting requirements: lockable medication trolley or cabinet, fridge temperature log (2-8C checked daily), room temperature log if required, controlled drugs stored in separate locked cupboard within locked storage, CD register with running balances, clear separation of different service users\' medications.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90045, 29, '29.4', 'There is evidence that staff have access to occupational health services for work-related health concerns, sickness absence management, and reasonable adjustments.', 'Occupational health service provider details, referral records, or occupational health reports with recommendations implemented.', NULL, NULL, 'Occupational health referral forms, occupational health reports with recommendations for adjustments, or evidence of reasonable adjustments made following occupational health advice.', '2025-12-15 02:01:49', NULL, 'Controlled drugs management records including: controlled drugs register with running balances, two-signature administration, regular stock checks, and disposal records.', 'CD register documenting: drug name and strength, date and time of administration, dose given, two staff signatures, running balance calculated after each entry, stock check signatures (weekly minimum), disposal records with two signatures and witness details, no unexplained discrepancies.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90046, 29, '29.5', 'There is evidence that staff receive debriefing and support following challenging incidents, safeguarding concerns, or traumatic events.', 'Debriefing records following incidents, evidence of support offered to staff involved in safeguarding investigations, or access to counselling after traumatic events.', NULL, NULL, 'Incident debriefing notes, staff support offered after safeguarding investigations documented, or reflective practice sessions following challenging incidents.', '2025-12-15 02:01:49', NULL, 'PRN (as required) medication protocols specifying: circumstances for use, maximum dose/frequency, non-pharmacological interventions tried first, and effectiveness monitoring.', 'PRN protocol for each as-required medication detailing: specific symptoms/situations requiring PRN (pain score above 3, anxiety symptoms, constipation), non-drug interventions to try first (repositioning, reassurance, offering fluids), maximum dose in 24 hours, minimum time between doses, how to assess effectiveness, when to seek medical review.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90047, 29, '29.6', 'There is evidence of effective sickness absence monitoring and management, including return-to-work interviews, absence triggers, and support for staff with health issues.', 'Sickness absence records, return-to-work interview forms, and evidence of support offered to staff with recurring absence or long-term sickness.', NULL, NULL, 'Sickness absence tracking system, return-to-work interview forms completed after every absence, occupational health referrals for staff with concerning absence patterns, or phased return-to-work plans.', '2025-12-15 02:01:49', NULL, 'Covert medication administration records (if applicable) showing: mental capacity assessment, best interests decision, prescriber approval, pharmacy involvement, and regular reviews.', 'Covert medication documentation containing: mental capacity assessment for medication decision, best interests meeting minutes with family/professionals, GP written authorisation for covert administration, pharmacist advice on suitable medications and administration method, care plan detailing how medication is disguised, monthly review of continued necessity.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90048, 29, '29.7', 'There is evidence of lone working risk assessments and safety measures for staff who work alone (e.g., night staff, community care workers).', 'Lone working risk assessments, lone working policy, and safety measures in place (e.g., check-in procedures, personal alarms, mobile phones).', NULL, NULL, 'Lone working risk assessment for night staff, lone working policy with check-in procedures, or personal safety devices provided to lone workers (alarms, mobile phones).', '2025-12-15 02:01:49', NULL, 'Staff medication competency records showing: initial training, competency assessment, supervision of practice, and regular updates/refresher training.', 'Staff training matrix showing: medication administration training completion date, competency assessment passed (observed practice, written test), supervision records (medication rounds observed by senior staff), annual refresher training, specific training for specialist administration (PEG feeding, insulin, inhalers).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (90049, 29, '29.8', 'There is evidence of proactive wellbeing initiatives offered to staff (e.g., wellbeing days, team-building activities, health promotion, mental health awareness training).', 'Records of wellbeing activities or initiatives offered to staff, such as wellbeing days, team events, health checks, or mental health awareness sessions.', NULL, NULL, 'Wellbeing day event records, team-building activity photos or feedback, mental health awareness training attendance, or health promotion initiatives (e.g., flu jabs, health checks).', '2025-12-15 02:01:49', NULL, 'Medication error reporting and analysis records showing: incident forms completed, investigation undertaken, learning identified, and actions to prevent recurrence.', 'Medication error documentation containing: incident report form detailing error (missed dose, wrong time, wrong person), immediate actions taken, investigation findings, root cause analysis, learning points identified, actions implemented (additional training, procedure changes), staff meeting minutes sharing learning, follow-up audit to check improvements.');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (1, 1, 'Respecting & Involving People', 'service_user', 'Ensuring person-centred care, dignity, respect, and involvement in decision-making', 'This section covers how well you respect service users\' choices, preferences, and dignity. Focus on person-centred care practices, communication, and involvement in care planning.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (2, 2, 'Consent', 'service_user', 'Obtaining and documenting valid consent, understanding MCA and DoLS', 'Assess how consent is obtained and recorded. Check understanding of Mental Capacity Act (MCA), Deprivation of Liberty Safeguards (DoLS), and Lasting Power of Attorney (LPA).', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (3, 3, 'Care and Welfare', 'service_user', 'Comprehensive care planning, holistic support, and meeting individual needs', 'Review care plans for comprehensiveness, person-centredness, and regular reviews. Ensure all aspects of wellbeing are addressed.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (4, 4, 'Meeting Nutritional Needs', 'service_user', 'Providing adequate nutrition, hydration, and dietary support', 'Check food quality, choice, hydration monitoring, dietary requirements, and nutritional risk assessments.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (5, 5, 'Co-operating with Providers', 'service_user', 'Working effectively with other healthcare and social care providers', 'Assess collabouration with GPs, hospitals, social workers, and other services. Check Memorandums of Understanding (MoU) and information sharing.', 'annually', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (6, 6, 'Safeguarding', 'service_user', 'Protecting service users from abuse, harm, and neglect', 'Review safeguarding policies, incident reporting, staff training, and protection procedures. Ensure all concerns are properly reported and investigated.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (7, 7, 'Infection Control', 'service_user', 'Preventing and controlling infections through proper hygiene and procedures', 'Check hand hygiene, PPE usage, cleaning schedules, waste disposal, and infection control policies. Review outbreak management procedures.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (8, 8, 'Management of Medicine', 'service_user', 'Safe ordering, storage, administration, and disposal of medications', 'Audit medication administration records (MAR), storage conditions, staff competency, and medication errors. Check PRN protocols and controlled drugs management.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (9, 9, 'Safety of Premises', 'service_user', 'Maintaining safe, accessible, and suitable premises', 'Assess building safety, fire safety, accessibility, maintenance, and environmental risk assessments. Check compliance with health and safety regulations.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (10, 10, 'Safety of Equipment', 'service_user', 'Ensuring all equipment is safe, maintained, and fit for purpose', 'Review equipment maintenance records, PAT testing, servicing schedules, and safe usage. Check hoists, beds, wheelchairs, and medical devices.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (14, 14, 'Quality Monitoring', 'service_user', 'Regular audits, quality assurance, and continuous improvement', 'Review audit schedules, action plans, quality improvement initiatives, and management oversight. Check how feedback is used to improve services.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (15, 15, 'Complaints', 'service_user', 'Handling complaints effectively and learning from feedback', 'Audit complaints log, response times, investigation quality, and lessons learned. Ensure accessible complaints procedures.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (16, 16, 'Records', 'service_user', 'Maintaining accurate, secure, and accessible records', 'Check record-keeping standards, data protection compliance, storage security, and accessibility. Review care plans, daily notes, and incident records.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (17, 17, 'Financial Management', 'service_user', 'Managing service user finances safely and transparently', 'Audit financial records, receipts, authorizations, and safeguarding of service user money. Check policies and procedures for handling finances.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (18, 18, 'Activities and Engagement', 'service_user', 'Providing meaningful activities and social engagement', 'Review activity programs, individual preferences, participation records, and community involvement. Ensure activities promote wellbeing and independence.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (19, 19, 'End of Life Care', 'service_user', 'Providing dignified, compassionate end of life care', 'Check advance care planning, pain management, family involvement, and dignity in dying. Review staff training and support.', 'annually', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (20, 20, 'Mental Health Support', 'service_user', 'Supporting mental health and emotional wellbeing', 'Assess mental health assessments, support plans, access to specialist services, and staff awareness. Check for person-centred mental health care.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (21, 21, 'Equality and Diversity', 'service_user', 'Promoting equality, respecting diversity, and preventing discrimination', 'Review policies, staff training, and practices around protected characteristics. Ensure inclusive care that respects cultural, religious, and personal preferences.', 'annually', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (22, 22, 'Governance and Leadership', 'service_user', 'Effective leadership, governance structures, and accountability', 'Assess management oversight, governance meetings, accountability frameworks, and leadership quality. Check registered manager presence and oversight.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (23, 1, 'Recruitment & Vetting', 'staff', 'Safe recruitment practices and pre-employment checks', 'Ensure all staff have completed DBS checks, references, identity verification, right to work checks, and professional registration checks where applicable.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (24, 2, 'Induction & Onboarding', 'staff', 'Comprehensive induction and onboarding for new staff', 'Check induction programs cover Care Certificate standards, policies, procedures, and shadowing. Ensure new staff are competent before working unsupervised.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (25, 3, 'Training & Development', 'staff', 'Ongoing training and professional development', 'Audit mandatory training compliance (safeguarding, infection control, medication, moving and handling, etc.) and specialist training needs. Check training matrix and certificates.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (26, 4, 'Supervision & Appraisal', 'staff', 'Regular supervision, appraisal, and performance management', 'Review supervision frequency (minimum every 6-8 weeks), quality of supervision records, annual appraisals, and personal development plans.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (27, 5, 'Competency & Observation', 'staff', 'Competency assessments and observational checks', 'Check competency assessments for medication, moving and handling, and other key tasks. Review spot checks and observational audits of practice.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (28, 6, 'Health & Wellbeing', 'staff', 'Supporting staff health, wellbeing, and fitness to practice', 'Review occupational health assessments, sickness absence management, stress risk assessments, and wellbeing support offered to staff.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (29, 7, 'Conduct & Capability', 'staff', 'Managing conduct, capability, and disciplinary issues', 'Audit disciplinary records, capability procedures, and professional conduct standards. Ensure fair and consistent management of performance issues.', 'annually', 1, '2025-12-14 13:01:22');
