INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (1, 1, 'Respecting & Involving People', 'service_user', 'Ensuring person-centred care, dignity, respect, and involvement in decision-making', 'This section covers how well you respect service users'' choices, preferences, and dignity. Focus on person-centred care practices, communication, and involvement in care planning.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (2, 2, 'Consent', 'service_user', 'Obtaining and documenting valid consent, understanding MCA and DoLS', 'Assess how consent is obtained and recorded. Check understanding of Mental Capacity Act (MCA), Deprivation of Liberty Safeguards (DoLS), and Lasting Power of Attorney (LPA).', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (3, 3, 'Care and Welfare', 'service_user', 'Comprehensive care planning, holistic support, and meeting individual needs', 'Review care plans for comprehensiveness, person-centredness, and regular reviews. Ensure all aspects of wellbeing are addressed.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (4, 4, 'Meeting Nutritional Needs', 'service_user', 'Providing adequate nutrition, hydration, and dietary support', 'Check food quality, choice, hydration monitoring, dietary requirements, and nutritional risk assessments.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (5, 5, 'Co-operating with Providers', 'service_user', 'Working effectively with other healthcare and social care providers', 'Assess collabouration with GPs, hospitals, social workers, and other services. Check Memorandums of Understanding (MoU) and information sharing.', 'annually', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (6, 6, 'Safeguarding', 'service_user', 'Protecting service users from abuse, harm, and neglect', 'Review safeguarding policies, incident reporting, staff training, and protection procedures. Ensure all concerns are properly reported and investigated.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (7, 7, 'Infection Control', 'service_user', 'Preventing and controlling infections through proper hygiene and procedures', 'Check hand hygiene, PPE usage, cleaning schedules, waste disposal, and infection control policies. Review outbreak management procedures.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (8, 8, 'Management of Medicine', 'service_user', 'Safe ordering, storage, administration, and disposal of medications', 'Audit medication administration records (MAR), storage conditions, staff competency, and medication errors. Check PRN protocols and controlled drugs management.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (9, 9, 'Safety of Premises', 'service_user', 'Maintaining safe, accessible, and suitable premises', 'Assess building safety, fire safety, accessibility, maintenance, and environmental risk assessments. Check compliance with health and safety regulations.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (10, 10, 'Safety of Equipment', 'service_user', 'Ensuring all equipment is safe, maintained, and fit for purpose', 'Review equipment maintenance records, PAT testing, servicing schedules, and safe usage. Check hoists, beds, wheelchairs, and medical devices.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (14, 14, 'Quality Monitoring', 'service_user', 'Regular audits, quality assurance, and continuous improvement', 'Review audit schedules, action plans, quality improvement initiatives, and management oversight. Check how feedback is used to improve services.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (15, 15, 'Complaints', 'service_user', 'Handling complaints effectively and learning from feedback', 'Audit complaints log, response times, investigation quality, and lessons learned. Ensure accessible complaints procedures.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (16, 16, 'Records', 'service_user', 'Maintaining accurate, secure, and accessible records', 'Check record-keeping standards, data protection compliance, storage security, and accessibility. Review care plans, daily notes, and incident records.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (17, 17, 'Financial Management', 'service_user', 'Managing service user finances safely and transparently', 'Audit financial records, receipts, authorizations, and safeguarding of service user money. Check policies and procedures for handling finances.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (18, 18, 'Activities and Engagement', 'service_user', 'Providing meaningful activities and social engagement', 'Review activity programs, individual preferences, participation records, and community involvement. Ensure activities promote wellbeing and independence.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (19, 19, 'End of Life Care', 'service_user', 'Providing dignified, compassionate end of life care', 'Check advance care planning, pain management, family involvement, and dignity in dying. Review staff training and support.', 'annually', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (20, 20, 'Mental Health Support', 'service_user', 'Supporting mental health and emotional wellbeing', 'Assess mental health assessments, support plans, access to specialist services, and staff awareness. Check for person-centred mental health care.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (21, 21, 'Equality and Diversity', 'service_user', 'Promoting equality, respecting diversity, and preventing discrimination', 'Review policies, staff training, and practices around protected characteristics. Ensure inclusive care that respects cultural, religious, and personal preferences.', 'annually', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (22, 22, 'Governance and Leadership', 'service_user', 'Effective leadership, governance structures, and accountability', 'Assess management oversight, governance meetings, accountability frameworks, and leadership quality. Check registered manager presence and oversight.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (23, 1, 'Recruitment & Vetting', 'staff', 'Safe recruitment practices and pre-employment checks', 'Ensure all staff have completed DBS checks, references, identity verification, right to work checks, and professional registration checks where applicable.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (24, 2, 'Induction & Onboarding', 'staff', 'Comprehensive induction and onboarding for new staff', 'Check induction programs cover Care Certificate standards, policies, procedures, and shadowing. Ensure new staff are competent before working unsupervised.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (25, 3, 'Training & Development', 'staff', 'Ongoing training and professional development', 'Audit mandatory training compliance (safeguarding, infection control, medication, moving and handling, etc.) and specialist training needs. Check training matrix and certificates.', 'monthly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (26, 4, 'Supervision & Appraisal', 'staff', 'Regular supervision, appraisal, and performance management', 'Review supervision frequency (minimum every 6-8 weeks), quality of supervision records, annual appraisals, and personal development plans.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (27, 5, 'Competency & Observation', 'staff', 'Competency assessments and observational checks', 'Check competency assessments for medication, moving and handling, and other key tasks. Review spot checks and observational audits of practice.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (28, 6, 'Health & Wellbeing', 'staff', 'Supporting staff health, wellbeing, and fitness to practice', 'Review occupational health assessments, sickness absence management, stress risk assessments, and wellbeing support offered to staff.', 'quarterly', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceSections` (`id`, `sectionNumber`, `sectionName`, `sectionType`, `description`, `tooltip`, `auditFrequency`, `isActive`, `createdAt`) VALUES (29, 7, 'Conduct & Capability', 'staff', 'Managing conduct, capability, and disciplinary issues', 'Audit disciplinary records, capability procedures, and professional conduct standards. Ensure fair and consistent management of performance issues.', 'annually', 1, '2025-12-14 13:01:22');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30173, 1, '1.1', 'There is evidence that care and support files express individual’s views, choices, and preferences. - Includes pre admission, care plans, daily notes and life history ', 'Evidence required: There is evidence on file that the care plans express views, choices and preferences...', NULL, NULL, 'Records express peoples’ views, choices, and preferences.', '2025-12-14 18:13:16', NULL, 'Individual care and support plan', 'Care plan containing: person''s goals and desired outcomes, daily routine preferences (wake time, meal times, bedtime), communication preferences and methods, cultural and religious needs with specific requirements, how independence will be maintained and promoted, specific interventions for each identified need, risk management strategies agreed with person, equipment or adaptations required, professional involvement (GP, district nurse, OT), review date set, person/representative involvement in planning, care plan can be first person (''I prefer...'') or third person as long as person-centred');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30174, 1, '1.8', 'There is evidence that care, and support files have been written with involvement from the person and or their representative. (Where able, the documentation should be agreed and signed by the person). 
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30175, 1, '1.9', 'The provider has a process in place to ensure that relevant documentation is available upon request in other formats i.e., easy read, large print, pictorial, other languages. ', 'The provider complies with the accessible information standard i.e. information is available in different formats upon request', NULL, NULL, 'The provider has a process in place to ensure that relevant documentation is available upon request in other formats i.e., easy read, large print, pictorial, other languages.', '2025-12-14 18:13:16', NULL, 'Mental capacity assessment for specific decisions', 'Capacity assessment record containing: specific decision being assessed (not general capacity), two-stage test applied (does person have impairment/disturbance of mind or brain, does this prevent them making this specific decision), evidence person can/cannot understand, retain, weigh and communicate decision, steps taken to support decision-making (accessible information, timing, location, support person), assessment outcome with rationale, assessor name and qualification, assessment date, review date if capacity may fluctuate, best interests process if lacking capacity');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30176, 1, '1.10', 'There is evidence that care, and support files are available and accessible to the person and their representatives in an appropriate format. ', 'Evidence required: There is evidence that staff have considered how the individual would like care to be delivered in a...', NULL, NULL, 'People are able to access their care and support file (either paper based or electronic) and in a format appropriate to their needs e.g. care plan in large print or pictorial format, use of alternative communication Methods i.e. Makaton', '2025-12-14 18:13:16', NULL, 'Consent records for care and treatment', 'Consent documentation showing: what person is consenting to (specific care or treatment), information provided about purpose, risks and benefits, person''s understanding confirmed, voluntary consent without coercion or undue influence, capacity to consent assessed, consent form signed and dated by person (or best interests decision if lacking capacity), ongoing consent reviewed and recorded, right to withdraw consent explained, consent reviewed if care needs change, note: consent doesn''t require signature if person lacks capacity or unable to sign, as long as involvement and agreement documented');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30177, 1, '1.11', 'There is evidence that care, and support files demonstrate privacy, dignity, and independence. In line with their personal choices, prefences and wishes (not just in relation to personal care) ', 'Care and support files promote the persons privacy and dignity.', NULL, NULL, 'care, and support files demonstrate privacy, dignity, and independence. In line with their personal choices, prefences and wishes (not just in relation to personal care)  documented on file.', '2025-12-14 18:13:16', NULL, 'Risk assessments and management plans', 'Risk assessment documents for: falls risk (using validated tool with score), skin integrity and pressure ulcer risk (Waterlow or similar), nutrition risk (MUST tool scored monthly minimum), moving and handling assessment, choking risk, behaviours that challenge, going out in community, self-neglect, financial abuse vulnerability; Each assessment showing: risk factors identified, risk level/score, control measures and interventions, equipment required, person''s views on managing risk, positive risk-taking balanced with safety, review frequency, assessment date and assessor');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30178, 1, '1.12', 'People who use the service felt treated with dignity and respect ', 'All people spoken with felt that they were treated with dignity and respect', NULL, NULL, 'People who use the service felt treated with dignity and respect', '2025-12-14 18:13:16', NULL, 'Communication assessment and plan', 'Communication profile containing: person''s preferred communication methods, sensory impairments (hearing, vision) and aids used, speech and language needs, understanding and comprehension level, non-verbal communication, communication aids or technology, how person expresses pain or distress, how to gain person''s attention, best times for communication, involvement of speech and language therapist if needed, accessible information standard compliance, staff guidance on communicating effectively with person');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30179, 1, '1.2', 'People were encouraged to maintain their independence and their choices and preferences are upheld ', 'Observations of practice evidence that people were encouraged to maintain their independence and their choices and preferences upheld', NULL, NULL, 'People were encouraged to maintain their independence and their choices and preferences are upheld', '2025-12-14 18:13:16', NULL, 'Nutrition and hydration care plan', 'Nutrition plan containing: dietary preferences and dislikes, cultural or religious dietary requirements, food allergies and intolerances, texture modification (soft, pureed, thickened fluids) with SALT assessment, assistance required with eating and drinking, adaptive equipment (plate guards, adapted cutlery), food fortification or supplements, MUST score and monitoring frequency, weight monitoring schedule, fluid intake monitoring if required, mealtime environment preferences, involvement of dietitian if needed');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30180, 1, '1.3', 'Staff can explain how people’s dignity and privacy are respected  ', 'All staff spoken with were able to explain how people’s dignity and privacy were respected (10 dignity do’s)', NULL, NULL, 'Staff can explain how people’s dignity and privacy are respected', '2025-12-14 18:13:16', NULL, 'Personal care plan', 'Personal care plan containing: bathing/showering preferences (frequency, time of day, bath or shower), continence needs and management (pads, catheter, toileting routine), skin care requirements, oral hygiene and dental care, hair care preferences, nail care, dressing preferences and level of support needed, cultural or religious requirements for personal care, same gender care preferences, privacy and dignity maintained, person''s independence promoted, equipment required (hoist, bath lift, commode)');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30181, 1, '1.4', 'Observations confirmed people are given information in an appropriate way to make decisions i.e., Clothing, meals, and activities ', 'All observations of practice evidenced that people are given information in an appropriate way to make decisions', NULL, NULL, 'Observations confirmed people are given information in an appropriate way to make decisions i.e., Clothing, meals, and activities', '2025-12-14 18:13:16', NULL, 'Mobility and moving and handling plan', 'Moving and handling assessment containing: mobility level and aids used (frame, stick, wheelchair), transfers (bed, chair, toilet, car) with equipment and number of staff, walking ability and distance, falls risk and history, moving and handling equipment required (hoist type and sling size, slide sheets, transfer board), safe working load checked, staff training in equipment use, person''s cooperation and understanding, environmental factors, physiotherapy or OT involvement, assessment review date');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30182, 1, '1.5', 'People confirmed that they feel supported by staff to make informed decisions ', 'All people spoken with felt supported by staff to make informed decisions', NULL, NULL, 'People confirmed that they feel supported by staff to make informed decisions', '2025-12-14 18:13:16', NULL, 'Induction checklist and competency sign-off records', 'Induction programme showing: organisational policies covered (safeguarding, health & safety, infection control, confidentiality, whistleblowing), shadowing hours completed with experienced staff, competency assessments in key tasks with supervisor sign-off, probationary review meetings scheduled, Care Certificate enrolment or completion certificate, employee and manager signatures confirming completion');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30183, 1, '1.6', 'People confirmed that they are supported to maintain relationships, access a variety of activities and the community ', 'All people spoken with felt supported to maintain relationships, access a variety of activities and the community', NULL, NULL, 'People confirmed that they are supported to maintain relationships, access a variety of activities and the community', '2025-12-14 18:13:16', NULL, 'Probationary period review documentation', 'Review records containing: performance assessment against agreed objectives, competency demonstrated in key duties, attendance and punctuality record, values and behaviours assessment, areas for development identified with support plan, decision to confirm employment or extend probation with reasons, employee and manager signatures, date of formal review meeting');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30184, 1, '1.7', 'Staff are observed to have positive and meaningful interactions with people who use the service ', 'All staff were observed to have positive and meaningful interactions with people who use the service.  Staff are at the same eye level to aid effective communication, staff are kind, compassionate, patient and responsive to the person’s needs.', NULL, NULL, 'Staff are observed to have positive and meaningful interactions with people who use the service', '2025-12-14 18:13:16', NULL, 'DBS Update Service subscription or re-checking schedule', 'For Update Service: DBS certificate numbers with Update Service subscription references, quarterly online status checks recorded with dates and outcomes, consent forms from employees; For re-checking: schedule showing 3-yearly DBS renewal dates, new certificates obtained before expiry, risk assessments if any gaps in coverage');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30186, 2, '2.1', 'There is evidence of appropriately signed consent for all relevant decisions where the person is deemed to have capacity. For example.
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30187, 2, '2.2', 'There is evidence of appropriately signed consent by the relevant LPA (Lasting Power of Attorney) where the person is deemed not to have capacity for a specific decision and there is a copy of the LPA on file.', 'Evidence required: Both care files reviewed referenced that they had an LPA in place, however there was no evidence of ...', NULL, NULL, 'All appropriate consent for all decisions are in place and signed by the LPA (with a copy of the relevant LPA).', '2025-12-14 18:13:16', NULL, 'Safeguarding protection plan', 'Protection plan containing: identified risks to person, actions to reduce risk and keep person safe, restrictions on alleged perpetrator (suspension, supervision, moved), increased monitoring or observations, changes to care plan, involvement of advocates or family, person''s views and wishes, multi-agency involvement (police, social worker, safeguarding team), review dates, responsibility for actions, person''s agreement to plan (or best interests if lacking capacity), plan effectiveness monitored');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30188, 2, '2.3', 'There is evidence of appropriate mental capacity assessments and best interest decisions where the person is deemed to not have capacity for a specific decision.  These decisions have been made in collabouration with other representatives and/or other relevant parties. For example, -
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30189, 2, '2.4', 'There is evidence that the service has DOLs in place as required which demonstrates that least restrictive measures have been considered and where conditions are in place these are being adhered to.', 'Where a DOLs has been granted a copy is held on file
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30190, 2, '2.5', 'There is a system in place to track, identify conditions and expiry date of DOLs.', 'We did not see evidence of a system in place to track, identify conditions and expiry date of DOLs.', NULL, NULL, 'There is a tracking system in place that records all relevant information e.g. person’s name, date applied for, date granted, expiry date, conditions, date CQC notification sent', '2025-12-14 18:13:16', NULL, 'Equality, diversity and inclusion policy', 'Policy containing: commitment to Equality Act 2010 protected characteristics, zero tolerance of discrimination and harassment, reasonable adjustments for staff and service users, cultural competence and awareness training, accessible information standard compliance, diverse recruitment practices, equality impact assessments, staff and service user consultation, complaints procedure, monitoring and reporting of equality data, senior management responsibility, annual review');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30191, 2, '2.6', 'Staff are observed obtaining consent from people.', 'All staff were observed obtaining consent from people', NULL, NULL, 'Staff are observed obtaining consent from people.', '2025-12-14 18:13:16', NULL, 'Confidentiality and data protection policy', 'Policy containing: UK GDPR and Data Protection Act 2018 compliance, lawful basis for processing personal data, information sharing protocols and consent requirements, data security measures (encryption, password protection, secure storage), access controls and need-to-know principle, retention and disposal schedules, data breach reporting procedures, subject access request process, staff training on confidentiality, Information Commissioner''s Office registration, Data Protection Officer details, review date');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30192, 2, '2.7', 'Staff can describe how they ensure the principles of the MCA and how it is put into practice in their daily work.', 'All staff spoken with were able to describe how they ensure the principles of the MCA and how it is put into practice in their daily work', NULL, NULL, 'Staff can describe how they ensure the principles of the MCA and how it is put into practice in their daily work.', '2025-12-14 18:13:16', NULL, 'Complaints policy and procedure', 'Policy containing: how to make a complaint (verbal, written, online), complaints officer identified, acknowledgement timescale (e.g. 3 working days), investigation process and timescales, independent advocacy support offered, resolution and response timescale, escalation to senior management, referral to Local Government and Social Care Ombudsman, learning from complaints and service improvement, annual complaints report, accessible formats available, display of policy in service');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30194, 3, '3.1', 'There is evidence that people have appropriate contact details of their care and support provider including relevant out of hour contact details, e.g. contact details are documented in a service user guide.', 'Evidence required: The service has a normal guide and easy read service user guide which is given to clients...', NULL, NULL, 'Up-to-date contact details are available i.e. recorded within the service user guide', '2025-12-14 18:13:16', NULL, 'Medication care plan and support', 'Medication care plan containing: person''s understanding and involvement in medication, level of support required (full administration, prompting, self-administration), self-administration risk assessment if applicable, how person prefers to take medication, best time and method, medication side effects to monitor, person''s capacity to consent to medication, covert medication protocol if applicable with best interests decision, pharmacy involvement, GP medication reviews (minimum annually), medication changes documented with reason');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30195, 3, '3.2', 'There is evidence of a fully completed pre assessment prior to the person accessing the service, covering all areas of care and support, including risk, that demonstrates the provider can meet the person’s needs.', 'Evidence required: In the first file we reviewed there was evidence of a “Gathering Information for the Initial Assessm...', NULL, NULL, 'Fully completed pre -admission assessment prior to the person accessing the service which covers all areas of need and risk is on file.', '2025-12-14 18:13:16', NULL, 'PRN (as required) medication protocol', 'PRN protocol for each medication containing: medication name and dose, specific indication for use (e.g. ''for anxiety when...'', ''for pain when...''), signs and symptoms requiring PRN, non-pharmacological interventions tried first, maximum dose in 24 hours, minimum time between doses, monitoring after administration, effectiveness recorded, when to seek medical advice, person''s views on PRN use, review of PRN usage patterns to identify unmet needs');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30196, 3, '3.3', 'There is evidence of documented life history/about me information.', 'Evidence required: In the first file viewed, there was very little information regarding the individual’s life history ...', NULL, NULL, 'There is evidence that life history information is recorded in full where applicable. 
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30197, 3, '3.4', 'There are relevant care and support plans in place to deliver safe and effective care, e.g.:
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30198, 3, '3.5', 'There is evidence that care, and support plans are strength based, person centred and promotes independence. ', 'Evidence required: There is evidence in both care and support file that support should be provided in a strength-based ...', NULL, NULL, 'Care and support plans are strength based, person centred and promotes independence', '2025-12-14 18:13:16', NULL, 'Covert medication best interests decision', 'Covert medication documentation containing: capacity assessment showing person lacks capacity to consent to medication, best interests meeting held with GP, pharmacist, family, care staff, decision that covert administration in person''s best interests, less restrictive options considered, how medication will be disguised, monitoring for effectiveness and side effects, pharmacy advice on safe administration, regular review of continued need, person''s previous wishes considered, DoLS consideration, documentation of each covert administration');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30199, 3, '3.6', 'There is evidence that care, and support plans include individual personalised outcomes. NB this may be as simple as maintaining a persons level of independence in relation to activities of daily living ', 'Evidence required: There is evidence throughout both care and support plans reviewed that they include outcomes that ar...', NULL, NULL, 'All care and support plans include individual personalised outcomes that are relevant, realistic and achievable.', '2025-12-14 18:13:16', NULL, 'Induction evaluation and feedback', 'Evaluation form completed by new employee containing: quality of induction programme rated, usefulness of shadowing and mentoring, clarity of role expectations, confidence in key tasks, areas where more support needed, suggestions for improvement, overall satisfaction score, feedback used to improve induction for future staff, evaluation reviewed by management');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30200, 3, '3.7', 'There is evidence of appropriate risk assessments to deliver safe and effective care, and there are actions recorded to mitigate risk. ', 'Evidence required: Both care plans reviewed did not evidence appropriate risk assessments for all areas of risk identif...', NULL, NULL, 'All appropriate risk assessments are in place and actions recorded to mitigate risk (which may be signposted and recorded in the support plan', '2025-12-14 18:13:16', NULL, 'Induction for bank and agency staff', 'Abbreviated induction checklist for bank/agency staff containing: site-specific orientation (layout, fire exits, equipment), introduction to current service users and their care needs, emergency procedures and contacts, key policies (safeguarding, whistleblowing, infection control), location of care plans and records, supervising staff member identified, shift-specific expectations, competency check in key tasks, completed on first shift before independent practice, signature of worker and supervisor');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30201, 3, '3.8', 'There is evidence that care and support plans are regularly reviewed and any change in needs are reflected accordingly. ', 'Evidence required: There is evidence that support plans are reviewed regularly and within the three months expected, ho...', NULL, NULL, 'Care and support plans are regularly reviewed (minimum 3 monthly or sooner if there is a change in need).  
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30202, 3, '3.9', 'There is evidence that risk assessments are regularly reviewed and any change in needs are reflected accordingly.
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30203, 3, '3.10', 'There is evidence that daily records are completed per shift or visit. ', 'Evidence required: 10 In the first file reviewed, there lacked evidence that daily records were completed per shift as ...', NULL, NULL, 'There is an entry within the daily records for each shift/visit', '2025-12-14 18:13:16', NULL, 'Daily care records showing: entries completed for each shift or visit, consistent recording across all staff, and evidence of daily care delivery.', 'Daily care log containing: entry for each shift (morning, afternoon, night) or each domiciliary visit, dated and timed entries, staff signature/initials on each entry, care tasks documented (personal care, meals, fluids, activities, mood, health observations), evidence all shifts/visits have corresponding records, no unexplained gaps in recording.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30204, 3, '3.11', 'There is evidence that daily entries are factual, dated, timed, legible, and signed by the member of staff(s), including their job role(s). ', 'Evidence required: Daily entries that were recorded were sometimes difficult to understand...', NULL, NULL, 'Daily entries are factual, dated, timed, legible, and signed by the staff members who have delivered the person care and support (including their job titles).', '2025-12-14 18:13:16', NULL, 'Daily care records demonstrating: factual objective recording, dated and timed entries, legible handwriting or typed entries, and staff signatures present.', 'Daily records showing: factual statements ("supported with shower", "ate full breakfast") not opinions, date and time on every entry, legible handwriting or electronic records, staff signature/initials identifying who provided care, professional language used, no abbreviations unless defined, corrections made appropriately (single line through error, initialled).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30205, 3, '3.12', 'There is evidence that daily entries provide a reflective account of care and support delivered and the persons health and wellbeing. NB Daily notes should be person centred ', 'Evidence required: The daily records are not completed and the notes that are completed are not consistently a reflecti...', NULL, NULL, 'Daily entries provide a reflective account of care and support delivered and the persons health and wellbeing', '2025-12-14 18:13:16', NULL, 'Daily care records providing: reflective accounts of care delivery, person''s responses to care, wellbeing observations, and meaningful interactions documented.', 'Daily records containing: reflective narrative ("appeared comfortable after repositioning", "enjoyed reminiscing about family"), person''s mood and engagement noted, responses to care interventions recorded, meaningful interactions documented (conversations, activities, preferences expressed), concerns or changes in condition highlighted, person-centred language used throughout.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30206, 3, '3.13', 'There is evidence that intervention charts are comprehensively completed and are signed and dated, e.g., food and fluid charts, repositioning charts, and pain charts. ', 'Evidence required: There was evidence of intervention charts for each individual reviewed, however not for all identifi...', NULL, NULL, 'Intervention charts are comprehensively completed and signed and dated', '2025-12-14 18:13:16', NULL, 'Intervention charts showing: comprehensive completion of all required fields, regular monitoring as specified, and evidence charts are kept up-to-date.', 'Intervention charts (repositioning, fluid balance, food intake, bowel monitoring) containing: all fields completed (date, time, action taken, staff signature), monitoring frequency as per care plan (e.g., 2-hourly repositioning), running totals calculated where required (fluid intake), no gaps in recording, charts replaced when full, archived charts retained.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30207, 3, '3.14', 'There is evidence that activity and social interactions are recorded.', 'Evidence required: In the first individuals file reviewed, there was little evidence of daily notes detailing activitie...', NULL, NULL, 'There is a detailed record of all activities and social interactions (daily record, activities sheet/log)', '2025-12-14 18:13:16', NULL, 'Activity records showing: activities offered and attended, social interactions documented, and evidence of meaningful engagement.', 'Activity documentation containing: daily activity participation recorded (group activities, one-to-one time, outings), social interactions noted in daily records (conversations with other residents, family visits, staff engagement), meaningful activities documented (enjoyed singing, participated in quiz, spent time in garden), engagement level recorded, reasons for non-participation noted.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30208, 3, '3.15', 'There is documented evidence of involvement from other health and social care professionals.', 'Evidence required: We did not evidence any documented involvement from health professionals on either file reviewed...', NULL, NULL, 'Records evidence that health and social care professionals have been appropriately involved and care and support plans have been updated to reflect the advice given or actions taken and that all relevant staff have been made aware (i.e. care, catering, housekeeping).', '2025-12-14 18:13:16', NULL, 'Multi-disciplinary involvement records showing: healthcare professional visits documented, advice received recorded, and implementation in care plans evidenced.', 'Professional involvement log containing: GP visits and advice documented, district nurse wound care visits recorded, physiotherapy input noted, occupational therapy assessments filed, dietician recommendations documented, SALT advice recorded, mental health professional involvement, social worker contact, specialist consultant input, evidence advice implemented in care plans.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30209, 3, '3.16', 'There is evidence of an accurately completed DNACPR/ ReSPECT document signed by a relevant health professional and easily accessible.', 'Evidence required: In the first file reviewed the individuals care plan states that he does have a DNACPR/ReSPECT form ...', NULL, NULL, 'Accurately completed DNACPR/ReSPECT which has been signed by the relevant healthcare professional and is easily accessible', '2025-12-14 18:13:16', NULL, 'DNACPR (Do Not Attempt Cardiopulmonary Resuscitation) or ReSPECT (Recommended Summary Plan for Emergency Care and Treatment) forms showing: completed by authorised clinician, signed and dated, service user/family involvement documented, and regular reviews.', 'DNACPR/ReSPECT documentation containing: form completed by senior clinician (GP, consultant, paramedic), clinical signature and GMC number, date of decision, mental capacity assessment recorded, discussion with service user/family documented, reasons for decision stated, review date set (usually annual or following significant change), form easily accessible for emergency services, care plan cross-references DNACPR status.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30210, 3, '3.17', 'People confirmed that the care and support received is carried out in a way that meets their needs in line with their personal preferences.', 'All people spoken with confirmed that the care and support they receive is carried out in a way that meets their needs and personal preferences.', NULL, NULL, 'People confirmed that the care and support received is carried out in a way that meets their needs in line with their personal preferences.', '2025-12-14 18:13:16', NULL, 'Service user feedback on care delivery showing: satisfaction with care received, care delivered according to preferences, and dignity and respect maintained.', 'Feedback records showing: service user comments in care reviews ("staff are kind and helpful", "care is provided how I like it"), satisfaction survey responses, residents'' meeting feedback on care quality, compliments received, care observations noting person appears comfortable and content, family feedback on care delivery, evidence concerns raised are addressed, advocacy involvement if needed.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30212, 4, '4.1', 'There is evidence of a varied menu which promotes healthy eating choices and acknowledges cultural and personal preferences.', 'The menu is varied and supports a nutritional and balanced diet that takes into account personal preferences (vegetarian options, meat, fish, selection of seasonal food and other alternatives)', NULL, NULL, 'a varied menu which promotes healthy eating choices and acknowledges cultural and personal preferences documented on file.', '2025-12-14 18:13:16', NULL, 'Policy or procedure document confirming 24-hour access to food and fluids, kitchen accessibility arrangements, and staff training records on food preparation.', 'Nutrition policy stating: kitchen accessible 24/7 or secure food storage in communal areas, staff trained in food hygiene and preparation, availability of snacks/drinks outside meal times, night staff access to facilities for preparing hot drinks and light meals.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30213, 4, '4.2', 'There is access to sufficient supplies of fresh food and fluids over a 24 hour period.', 'Staff confirm that food and fluids are available 24/7 i.e., staff have access and facilities to prepare food and drink outside of kitchen staff hours', NULL, NULL, 'There is access to sufficient supplies of fresh food and fluids over a 24 hour period.', '2025-12-14 18:13:16', NULL, 'Individual nutritional support plans containing: known allergies, food preferences, dislikes, cultural/religious dietary requirements, and IDDSI (International Dysphagia Diet Standardisation Initiative) levels where applicable.', 'Nutritional care plan documenting: "No known allergies" or specific allergies listed (nuts, dairy, gluten), food preferences (likes fish, dislikes mushrooms), cultural needs (halal meat, no pork), IDDSI level if required (Level 4 pureed, Level 1 thin liquids), portion size preferences.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30214, 4, '4.3', 'There are nutritional support plans in place that include food allergies, likes, dislikes and any dietary restrictions including IDDSI.', 'Evidence required: Nutritional support plan did include likes and dislikes and dietary requirements...', NULL, NULL, 'Nutritional support plans include food allergies, likes, dislikes and any dietary restrictions including IDDSI', '2025-12-14 18:13:16', NULL, 'Nutritional support plans incorporating professional dietary advice from: dietician assessments, Speech and Language Therapy (SALT) recommendations, or other specialist input, with dates of assessments and review dates.', 'Care plan section containing: dietician report dated within last 6 months with specific dietary recommendations, SALT assessment detailing swallowing difficulties and safe food/fluid consistencies, implementation plan showing how professional advice is followed in daily practice.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30215, 4, '4.4', 'Where required there is a nutritional support plan which details any other dietary advice e.g., dietician/SALT.', 'Nutritional support plans detail up-to-date and accurate advice from dietician, SALT etc.', NULL, NULL, 'Where required there is a nutritional support plan which details any other dietary advice e.g., dietician/SALT.', '2025-12-14 18:13:16', NULL, 'Nutritional support plans documenting MUST (Malnutrition Universal Screening Tool) score, risk category (low/medium/high), and specific actions to address identified nutritional risks.', 'MUST assessment showing: calculated score (0=low risk, 1=medium risk, 2+=high risk), risk category clearly stated, action plan detailing: food fortification, supplement drinks, increased meal frequency, referral to dietician, weight monitoring schedule.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30216, 4, '4.5', 'Where required there is a nutritional support plan which details the MUST level and any actions to be taken to mitigate the risk.', 'Nutritional support plan details the MUST level and any actions to be taken to mitigate risk', NULL, NULL, 'Where required there is a nutritional support plan which details the MUST level and any actions to be taken to mitigate the risk.', '2025-12-14 18:13:16', NULL, 'Completed MUST assessment tools for each service user requiring nutritional monitoring, showing: calculation workings, BMI, weight loss percentage, acute disease effect score, and evidence of monthly reviews or reviews following change in condition.', 'MUST form containing: current weight and height measurements, BMI calculation, percentage weight loss over 3-6 months, acute disease score if applicable, total MUST score, date completed, staff signature, evidence of monthly review dates or re-assessment following illness/hospital admission.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30217, 4, '4.6', 'Where required there is a MUST tool for each person which is accurately completed and reviewed in line with the tool. NB to be completed at least monthly or following a change in need ', 'MUST is accurately completed where required and regularly reviewed', NULL, NULL, 'Where required there is a MUST tool for each person which is accurately completed and reviewed in line with the tool. NB to be completed at least monthly or following a change in need', '2025-12-14 18:13:16', NULL, 'Displayed menu that is current (showing correct week/date), clearly visible to service users and visitors, and accurately reflects meals being served.', 'Menu board or printed menu displayed in communal dining area showing: current week dates, daily meal options matching what is actually served, special dietary options clearly marked, updated regularly to reflect seasonal changes or special occasions.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30218, 4, '4.7', 'There was a menu displayed that is visible and accurate.', 'The menu is visible and accurate', NULL, NULL, 'There was a menu displayed that is visible and accurate.', '2025-12-14 18:13:16', NULL, 'Service user feedback records, meeting minutes, or care plan notes evidencing that individual food and drink requests are accommodated.', 'Residents'' meeting minutes recording discussion of menu preferences, care plan notes stating individual requests ("prefers Earl Grey tea", "likes decaf coffee after 2pm"), kitchen records showing stock of specialist items, feedback forms showing requests have been actioned.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30219, 4, '4.8', 'People confirmed that person requests of food and drinks can be accommodated e.g., flavoured tea, decaffeinated coffee.', 'All people spoken with confirmed that individual requests of food and drink are accommodated', NULL, NULL, 'People confirmed that person requests of food and drinks can be accommodated e.g., flavoured tea, decaffeinated coffee.', '2025-12-14 18:13:16', NULL, 'Observation records or quality monitoring notes documenting mealtime experience including: dining environment, staff interaction, support provided, use of adaptive equipment, and service user engagement.', 'Mealtime observation form noting: calm and unhurried atmosphere, appropriate music/noise levels, staff sitting with service users during meals, use of adapted cutlery/crockery where needed, show plates used for choice, table settings appropriate, staff promoting independence whilst offering assistance as needed.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30220, 4, '4.9', 'There was a positive person-centred mealtime experience.', 'Observations of mealtime were a positive experience e.g. calm, relaxed, conducive, and enjoyable experience, staff engaged well, supported appropriately whilst promoting independence, use of adapted cutlery etc, show plates, appropriate table wear.', NULL, NULL, 'There was a positive person-centred mealtime experience.', '2025-12-14 18:13:16', NULL, 'Observation records or quality monitoring notes documenting mealtime experience including: dining environment, staff interaction, support provided, use of adaptive equipment, and service user engagement.', 'Mealtime observation form noting: calm and unhurried atmosphere, appropriate music/noise levels, staff sitting with service users during meals, use of adapted cutlery/crockery where needed, show plates used for choice, table settings appropriate, staff promoting independence whilst offering assistance as needed, positive interactions and conversation during meals.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30222, 5, '5.1', 'Where there is shared involvement from another provider this is clearly documented and there are clear processes in place to share information in a timely manner.', 'In the second file reviewed, care plans reference CMHT and District Nurse involvement but there was no evidence any information stored on file.', NULL, NULL, 'Where more than one provider is supporting a person there is a clearly documented process in place to share information in a timely manner (memorandum of understanding, partnership protocol)', '2025-12-14 18:13:17', NULL, 'Activity programme or schedule showing variety of activities offered, including: group activities, one-to-one sessions, community access, and activities suitable for different ability levels.', 'Monthly activity calendar displaying: daily group activities (arts/crafts, quizzes, music sessions), weekly outings (garden centre, café visits), one-to-one time slots, activities for varying abilities (seated exercise, reminiscence, sensory activities), evening/weekend activities, special events.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30223, 5, '5.2', 'There is a Must do Care Plan/ Hospital Passport reflective of current need.', 'Evidence required: There is a MUST Do Hospital Passport in place for both individuals that is reflective of their needs...', NULL, NULL, 'Must do care plan/hospital passport in place which is reflective of the persons need, fully completed, and reviewed', '2025-12-14 18:13:17', NULL, 'Activity participation records showing: which service users attended activities, level of engagement, enjoyment observed, and any barriers to participation addressed.', 'Activity attendance log recording: date, activity type, names of participants, engagement level (actively participated/observed/declined), notes on enjoyment ("smiled throughout", "requested to do again"), reasons for non-participation (unwell, preferred to rest), follow-up actions.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30224, 5, '5.3', 'People confirmed that they are supported to access other social care/health services.', 'All people spoken with confirmed that they are supported to access other social care/health services i.e. chiropodist, hairdresser, opticians.', NULL, NULL, 'People confirmed that they are supported to access other social care/health services.', '2025-12-14 18:13:17', NULL, 'Service user feedback on activities through: verbal feedback records, satisfaction surveys, residents'' meeting minutes, or care plan reviews showing activities are meaningful and enjoyable.', 'Feedback documentation showing: residents'' meeting minutes discussing activity preferences, satisfaction survey results on activity programme, care plan review notes recording service user comments ("enjoys gardening club", "would like more music sessions"), evidence of changes made based on feedback.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30226, 6, '6.1', 'There is a safeguarding policy in place that reflects the North Yorkshire multiagency safeguarding policy.', 'Evidence required: The service has a safeguarding policy and procedure in place...', NULL, NULL, 'There is a safeguarding policy in place that reflects the North Yorkshire multiagency safeguarding policy', '2025-12-14 18:13:17', NULL, 'Healthcare professional visit records showing: GP reviews, district nurse visits, specialist consultations, dates of visits, advice given, and actions taken.', 'Healthcare visit log documenting: GP annual review date, district nurse wound care visits (dates and treatment provided), chiropodist appointments, optician visits, dentist check-ups, physiotherapy sessions, record of advice given and how implemented in care plan.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30227, 6, '6.12', 'There is a record of DBS status for visiting professionals (e.g., chiropodist, hairdresser, entertainers).', 'There is a record of DBS status for all visiting professionals (e.g., chiropodist, hairdresser, entertainers', NULL, NULL, 'There is a record of DBS status for visiting professionals (e.g., chiropodist, hairdresser, entertainers).', '2025-12-14 18:13:17', NULL, 'Care plans containing specific guidance on managing health conditions, including: monitoring requirements, signs of deterioration, escalation procedures, and preventative care measures.', 'Condition-specific care plan detailing: diabetes management (blood sugar monitoring frequency, target ranges, hypo/hyper symptoms, treatment protocol), COPD management (inhaler regime, breathlessness management, when to seek medical help), dementia care (communication strategies, behaviour triggers, calming techniques).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30228, 6, '6.13', 'There is a copy of relevant public liability insurance for visiting professionals (e.g., chiropodist, hairdresser, entertainers).', 'There is a copy of relevant up-to-date public liability insurance for visiting professionals (e.g., chiropodist, hairdresser, entertainers).', NULL, NULL, 'There is a copy of relevant public liability insurance for visiting professionals (e.g., chiropodist, hairdresser, entertainers).', '2025-12-14 18:13:17', NULL, 'Records of health monitoring including: vital signs observations (blood pressure, temperature, pulse, respirations), weight monitoring, fluid balance charts, food intake records, where clinically indicated.', 'Monitoring charts showing: weekly weight records with trend analysis, daily fluid balance charts for at-risk individuals, food intake monitoring for nutritional concerns, vital signs observations with normal ranges indicated, dates and staff signatures, evidence of escalation when outside normal parameters.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30229, 6, '6.14', 'There is an accurate and up to date safeguarding log in place which includes documented actions and outcome.', 'Evidence required: There is a safeguarding log in place this only includes the date and number and reason for sending...', NULL, NULL, 'There is an accurate and up to date safeguarding log kept which includes documented actions and outcome', '2025-12-14 18:13:17', NULL, 'Continence assessment and management plans documenting: continence status, type of products used, toileting routine, skin integrity checks, and dignity considerations.', 'Continence care plan containing: continence assessment outcome, product type and size, toileting schedule (prompted every 2 hours), night-time routine, skin care regime, dignity measures (door closed, appropriate language), review dates, evidence of specialist continence nurse involvement if needed.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30230, 6, '6.2', 'Safeguarding notifications are submitted appropriately and in a timely way (48hrs).', 'Safeguarding notifications are always submitted appropriately and in a timely way', NULL, NULL, 'Safeguarding notifications are submitted appropriately and in a timely way (48hrs).', '2025-12-14 18:13:17', NULL, 'Tissue viability assessments and care plans for service users at risk of or with pressure damage, including: Waterlow score, repositioning schedule, pressure-relieving equipment, skin inspection routine.', 'Pressure care plan documenting: Waterlow score calculation, risk level (low/medium/high/very high), repositioning schedule (2-hourly turns, position chart), pressure-relieving equipment in use (air mattress, cushions), skin inspection frequency, body map showing any existing pressure areas, wound care regime if applicable.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30231, 6, '6.3', 'There is an accurate and up to date risk notification log kept which documents actions and outcomes.', 'Evidence required: There was no risk notification log in place on review...', NULL, NULL, 'There is an accurate and up to date risk notification log kept which documents actions and outcomes', '2025-12-14 18:13:17', NULL, 'Falls risk assessments and falls prevention care plans showing: falls risk score, identified risk factors, preventative measures, post-falls protocol, and equipment provided.', 'Falls care plan containing: falls risk assessment score, risk factors identified (poor mobility, postural hypotension, medication side effects), prevention strategies (walking aid provided, non-slip footwear, adequate lighting, call bell within reach), post-falls procedure, lying and standing blood pressure if indicated.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30232, 6, '6.4', 'Risk notifications are submitted appropriately and in a timely way (48hrs)', 'Risk notifications are submitted appropriately and in a timely way (48hrs)', NULL, NULL, 'Risk notifications are submitted appropriately and in a timely way (48hrs)', '2025-12-14 18:13:17', NULL, 'Mobility assessments and moving and handling care plans documenting: mobility level, equipment required, number of staff needed, transfer techniques, and physiotherapy input.', 'Moving and handling plan detailing: mobility assessment outcome, walking aid type (Zimmer frame, walking stick), transfer method (stand aid, hoist type and sling size), number of carers required (1 person assist, 2 person transfer), specific techniques to use, physiotherapy recommendations, equipment maintenance records.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30233, 6, '6.5', 'There is an accurate and up to date CQC log which documents actions and outcomes', 'Evidence required: The CQC notifications and safeguarding submissions are documented on the same log...', NULL, NULL, 'There is an accurate and up to date CQC notification log kept which documents actions and outcomes', '2025-12-14 18:13:17', NULL, 'Oral health assessments and mouth care plans showing: dental health status, denture care requirements, mouth care routine, and dental professional involvement.', 'Oral care plan documenting: oral health assessment findings, natural teeth or dentures, mouth care routine (frequency, products used), denture cleaning regime, dental check-up dates, referral to dentist if concerns identified, special considerations (dry mouth, swallowing difficulties affecting oral care).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30234, 6, '6.6', 'CQC notifications are submitted appropriately and in a timely way', 'CQC notifications are submitted appropriately and in a timely way (ASAP)', NULL, NULL, 'CQC notifications are submitted appropriately and in a timely way', '2025-12-14 18:13:17', NULL, 'Sensory impairment assessments and support plans for service users with hearing or visual impairments, including: aids provided, communication strategies, and specialist involvement.', 'Sensory care plan containing: hearing assessment (hearing aid user, type and settings), visual assessment (glasses prescription, visual field limitations), communication strategies (speak clearly facing person, written information in large print), equipment (hearing aid batteries, glasses cleaning), audiology/optician visit records.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30235, 6, '6.7', 'There is evidence of completed Herbert Protocols when required', 'Evidence required: The service informed us that they don’t support anyone currently that requires a Herbert protocol...', NULL, NULL, 'There is evidence of completed Herbert Protocols in place where required', '2025-12-14 18:13:17', NULL, 'Mental health and wellbeing assessments and support plans documenting: mental health conditions, psychological support provided, mood monitoring, and mental health professional involvement.', 'Mental health care plan detailing: diagnosed conditions (depression, anxiety, dementia-related distress), mood monitoring approach, psychological interventions (reminiscence therapy, validation techniques), mental health professional input (CPN, psychiatrist, psychologist), medication for mental health, activities supporting wellbeing.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30236, 6, '6.8', 'The safeguarding Policy and procedure are available to all staff.', 'Evidence required: The safeguarding policy is available to all staff, however the provider is to ensure that the policy...', NULL, NULL, 'The safeguarding policy and procedure is easily accessible to all staff.', '2025-12-14 18:13:17', NULL, 'Pain assessment and management plans showing: pain assessment tool used, pain levels recorded, pain relief provided, and effectiveness monitoring.', 'Pain management plan documenting: pain assessment tool (Abbey Pain Scale for non-verbal, numerical rating scale), pain location and description, pain triggers and relieving factors, analgesic regime (regular and PRN medications), non-pharmacological pain relief (positioning, heat pads, distraction), effectiveness reviews.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30237, 6, '6.9', 'There is safeguarding information / poster with up-to-date contact information on display or available in the service', 'There is safeguarding information / poster with up-to-date contact information on display or available within the service.', NULL, NULL, 'There is safeguarding information / poster with up-to-date contact information on display or available in the service', '2025-12-14 18:13:17', NULL, 'End of life care plans and advance care planning documentation showing: service user wishes, DNACPR status, preferred place of care, symptom management, and family involvement.', 'End of life care plan containing: advance care plan discussions recorded, DNACPR form (if in place), preferred place of death, spiritual/cultural wishes, symptom management plan, anticipatory medications prescribed, family contact arrangements, funeral wishes if discussed, regular review dates.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30238, 6, '6.10', 'Staff were observed supporting people to maintain independence and safety e.g., transfers, nutrition and hydration.', 'Staff were observed supporting people to maintain their independence and safety e.g., transfers, nutrition, and hydration.', NULL, NULL, 'Staff were observed supporting people to maintain independence and safety e.g., transfers, nutrition and hydration.', '2025-12-14 18:13:17', NULL, 'Hospital transfer records and hospital passports ensuring continuity of care, including: current care needs, medication list, communication needs, and important personal information.', 'Hospital passport document containing: personal details, communication needs (hearing aid, glasses, language), mobility status, continence management, nutritional needs, current medication list, health conditions, allergies, important routines, family contact details, what matters to the person.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30239, 6, '6.11', 'Staff can explain how to identify and prevent abuse and what action to take if they suspect abuse has occurred.', 'Staff can explain how to identify and prevent abuse and what action to take if they suspect abuse has occurred', NULL, NULL, 'Staff can explain how to identify and prevent abuse and what action to take if they suspect abuse has occurred.', '2025-12-14 18:13:17', NULL, 'Service user feedback on health and wellbeing support through: care reviews, satisfaction surveys, or recorded conversations showing people feel their health needs are met.', 'Feedback records showing: care plan review meetings with service user/family comments on health care, satisfaction survey responses regarding health support, recorded conversations ("I feel well looked after", "staff help me with my tablets"), evidence of concerns raised and actions taken.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30241, 7, '7.1', 'There is an up-to-date Infection Prevention and Control Policy and procedure in place.NB If concerns re content of policy refer to IPC team ', 'There is an up-to-date Infection Prevention and Control Policy and procedure in place', NULL, NULL, 'There is an up-to-date Infection Prevention and Control Policy and procedure in place.NB If concerns re content of policy refer to IPC team', '2025-12-14 18:13:17', NULL, 'Medication Administration Records (MARs) showing: legible entries, all administrations signed, gaps explained with codes, allergies recorded, photographs present, and regular audits completed.', 'MAR chart containing: service user photograph, known allergies clearly recorded, medication details (name, strength, dose, route, frequency), administration signatures in all boxes or explanation codes for gaps (R=refused, H=hospital, NH=not required), running balance for controlled drugs, monthly audit signature.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30242, 7, '7.2', 'There are cleaning schedules in place that demonstrates that all areas of the service are regularly cleaned.', 'There are cleaning schedules in place that demonstrates that all areas of the service are regularly cleaned', NULL, NULL, 'There are cleaning schedules in place that demonstrates that all areas of the service are regularly cleaned.', '2025-12-14 18:13:17', NULL, 'Medication storage arrangements showing: locked storage (cabinet/trolley/room), temperature monitoring records, controlled drugs register, and separation of internal/external medications.', 'Medication storage meeting requirements: lockable medication trolley or cabinet, fridge temperature log (2-8°C checked daily), room temperature log if required, controlled drugs stored in separate locked cupboard within locked storage, CD register with running balances, clear separation of different service users'' medications.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30243, 7, '7.3', 'There are effective systems and records in place to ensure that equipment is clean.', 'There are effective systems and records in place to ensure that equipment is clean.', NULL, NULL, 'There are effective systems and records in place to ensure that equipment is clean.', '2025-12-14 18:13:17', NULL, 'Controlled drugs management records including: controlled drugs register with running balances, two-signature administration, regular stock checks, and disposal records.', 'CD register documenting: drug name and strength, date and time of administration, dose given, two staff signatures, running balance calculated after each entry, stock check signatures (weekly minimum), disposal records with two signatures and witness details, no unexplained discrepancies.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30244, 7, '7.4', 'The environment is clean and there are no malodours.', 'The environment is clean and there are no malodours', NULL, NULL, 'The environment is clean and there are no malodours.', '2025-12-14 18:13:17', NULL, 'PRN (as required) medication protocols specifying: circumstances for use, maximum dose/frequency, non-pharmacological interventions tried first, and effectiveness monitoring.', 'PRN protocol for each as-required medication detailing: specific symptoms/situations requiring PRN (pain score above 3, anxiety symptoms, constipation), non-drug interventions to try first (repositioning, reassurance, offering fluids), maximum dose in 24 hours, minimum time between doses, how to assess effectiveness, when to seek medical review.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30245, 7, '7.5', 'PPE is readily available throughout the service and in date (gloves, aprons, masks, hand sanitiser etc.).', 'PPE is readily available throughout the service and in date (gloves, aprons, masks, hand sanitiser etc.).', NULL, NULL, 'PPE is readily available throughout the service and in date (gloves, aprons, masks, hand sanitiser etc.).', '2025-12-14 18:13:17', NULL, 'Covert medication administration records (if applicable) showing: mental capacity assessment, best interests decision, prescriber approval, pharmacy involvement, and regular reviews.', 'Covert medication documentation containing: mental capacity assessment for medication decision, best interests meeting minutes with family/professionals, GP written authorisation for covert administration, pharmacist advice on suitable medications and administration method, care plan detailing how medication is disguised, monthly review of continued necessity.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30246, 7, '7.6', 'All staff were observed following appropriate IPC measures including food handling, hand hygiene of people before and after meals.', 'All staff were observed following appropriate IPC measures including food handling, hand hygiene of people before and after meals', NULL, NULL, 'All staff were observed following appropriate IPC measures including food handling, hand hygiene of people before and after meals.', '2025-12-14 18:13:17', NULL, 'Staff medication competency records showing: initial training, competency assessment, supervision of practice, and regular updates/refresher training.', 'Staff training matrix showing: medication administration training completion date, competency assessment passed (observed practice, written test), supervision records (medication rounds observed by senior staff), annual refresher training, specific training for specialist administration (PEG feeding, insulin, inhalers).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30247, 7, '7.7', 'Staff can explain how to prevent and control the spread of infection.', 'Staff can explain how to prevent and control the spread of infection.', NULL, NULL, 'Staff can explain how to prevent and control the spread of infection.', '2025-12-14 18:13:17', NULL, 'Medication error reporting and analysis records showing: incident forms completed, investigation undertaken, learning identified, and actions to prevent recurrence.', 'Medication error documentation containing: incident report form detailing error (missed dose, wrong time, wrong person), immediate actions taken, investigation findings, root cause analysis, learning points identified, actions implemented (additional training, procedure changes), staff meeting minutes sharing learning, follow-up audit to check improvements.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30248, 7, '7.8', 'Staff can explain how they ensure the safe disposal of waste.', 'Staff can explain how they ensure the safe disposal of waste.', NULL, NULL, 'Staff can explain how they ensure the safe disposal of waste.', '2025-12-14 18:13:17', NULL, 'Medication reviews by healthcare professionals showing: GP medication reviews (at least annually), pharmacy input, medication changes documented, and care plan updates following reviews.', 'Healthcare records showing: annual GP medication review date, pharmacy medication review if care home, record of any medication changes (started, stopped, dose altered), rationale for changes documented, MAR chart updated, care plan amended to reflect new regime, family informed of significant changes.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30250, 8, '8.1', 'There is a Medication Policy and procedure in place.', 'There is a Medication Policy and procedure in place.', NULL, NULL, 'There is a Medication Policy and procedure in place.', '2025-12-14 18:13:17', NULL, 'Staff safeguarding training records showing: initial induction training, level-appropriate training (Level 2 minimum for care staff), regular updates, and competency assessment.', 'Training matrix showing: safeguarding adults training completion dates for all staff, training level achieved (Level 2 for care staff, Level 3 for managers), annual refresher training, training content (recognising abuse, reporting procedures, whistleblowing), assessment/quiz results, specific training on MCA/DoLS, financial abuse prevention.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30251, 8, '8.4', 'There is a process in place that confirms the current medication prescription of a new person to the service.', 'There is a process in place that confirms the current medication prescription of a new individual to the service. (e.g. checking with GP, checking prescription labels etc).', NULL, NULL, 'There is a process in place that confirms the current medication prescription of a new person to the service.', '2025-12-14 18:13:17', NULL, 'Safeguarding concern records showing: concerns identified, immediate actions taken, referrals made to local authority, investigation outcomes, and lessons learned.', 'Safeguarding incident file containing: initial concern record (date, nature of concern, person involved), immediate safeguarding actions (separation of alleged perpetrator, medical attention), local authority safeguarding referral form and submission evidence, safeguarding investigation outcome, strategy meeting minutes if held, protection plan implemented, lessons learned and service improvements.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30252, 8, '8.8', 'There is evidence that all staff who are responsible for administering medication have completed up to date medication training.', 'Evidence required: The service provided an up-to-date training matrix prior to assessment that evidenced that all staff...', NULL, NULL, 'There is evidence that all staff who are responsible for administering medication have completed up to date medication training.', '2025-12-14 18:13:17', NULL, 'Whistleblowing policy and evidence that staff know how to raise concerns, including: policy accessibility, staff training, external reporting contacts (CQC, local authority), and protection from detriment.', 'Whistleblowing policy containing: clear definition of whistleblowing, how to raise concerns (line manager, senior management, external bodies), contact details for CQC, local authority, and independent whistleblowing helplines, protection from victimisation statement, staff handbook reference, staff meeting minutes discussing whistleblowing, staff survey showing awareness.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30253, 8, '8.9', 'There is evidence that all staff who are responsible for administering medication have completed an up-to-date medication competency assessment.  This woud also include care staff responsible for admiistering creams and staff who are a second signatory/ witness for CDs  NB the person undertaing competency assessments should have up to date medication training and an indate competency assessment. Their competency assessmentcan be completed by a Senior member of staff, a peer at Registered manager', 'There is evidence that all staff who are responsible for administering medication have completed an up-to-date medication competency assessment (including care staff responsible for administering creams', NULL, NULL, 'all staff who are responsible for administering medication have completed an up-to-date medication competency assessment.  This woud also include care staff responsible for admiistering creams and staff who are a second signatory/ witness for CDs  NB the person undertaing competency assessments should have up to date medication training and an indate competency assessment. Their competency assessmentcan be completed by a Senior member of staff, a peer at Registered manager documented on file.', '2025-12-14 18:13:17', NULL, 'Deprivation of Liberty Safeguards (DoLS) applications and authorisations showing: DoLS assessments completed, applications submitted to local authority, current authorisations held, and conditions complied with.', 'DoLS documentation containing: DoLS screening assessment, application form submitted to supervisory body with receipt confirmation, current DoLS authorisation certificate with expiry date, any conditions attached to authorisation, evidence conditions are met (e.g., regular reviews, family contact maintained), renewal applications submitted in timely manner.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30254, 8, '8.10', 'Medication trolley is locked and securely tethered. ( When not in use)', 'Medication trolley is locked and securely tethered (when not in use).', NULL, NULL, 'Medication trolley is locked and securely tethered. ( When not in use)', '2025-12-14 18:13:17', NULL, 'Mental Capacity Act assessments showing: decision-specific capacity assessments, two-stage test applied (understand, retain, use/weigh, communicate), assessor details, and review dates.', 'MCA assessment form documenting: specific decision being assessed (consent to care, medication, accommodation), two-stage test applied (can person understand information, retain it, use and weigh it, communicate decision), evidence supporting conclusion, assessor name and role, date of assessment, review date set, how decision will be made if person lacks capacity.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30255, 8, '8.11', 'The Controlled drugs are stored in line with The Misuse of Drugs Act 1971.', 'Controlled drugs are stored in line with The Misuse of Drugs Act 1971', NULL, NULL, 'The Controlled drugs are stored in line with The Misuse of Drugs Act 1971.', '2025-12-14 18:13:17', NULL, 'Best Interests decision records showing: decision required, people consulted, factors considered, least restrictive option chosen, and rationale documented.', 'Best Interests meeting minutes/form containing: decision to be made, MCA assessment confirming lack of capacity, people consulted (family, advocates, professionals), person''s past and present wishes considered, factors for and against options, least restrictive option selected, rationale for decision, how decision will be implemented, review date, signatures of participants.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30256, 8, '8.12', 'Topical medication is stored appropriately e.g., separate to ingestible medications. ', 'Topical medication is stored appropriately e.g. separate to ingestible medications.
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30257, 8, '8.14', 'All medication is stored per manufacturer’s instructions e.g., refrigeration.', '', NULL, NULL, 'All medication is stored per manufacturer’s instructions e.g., refrigeration.', '2025-12-14 18:13:17', NULL, 'Restraint and restrictive practice records showing: risk assessments, least restrictive alternatives considered, legal authority (MCA/DoLS), monitoring, and regular reviews.', 'Restrictive practice documentation containing: risk assessment justifying restriction (bed rails, locked doors, sensor mats), alternatives considered and tried, legal authority (DoLS authorisation, best interests decision), care plan detailing restriction and monitoring requirements, incident records if restriction used, monthly review of continued necessity, reduction plan where possible.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30258, 8, '8.15', 'There are no food or biological items stored in an identified medication only refrigerator.', 'There are no food or biological items stored in an identified medication only refrigerator', NULL, NULL, 'There are no food or biological items stored in an identified medication only refrigerator.', '2025-12-14 18:13:17', NULL, 'Safeguarding supervision records for staff showing: regular supervision discussing safeguarding awareness, concerns raised, decision-making, and reflective practice.', 'Supervision notes containing: safeguarding discussion agenda item, staff understanding of safeguarding procedures checked, any concerns discussed, decision-making in complex situations explored, reflective practice on safeguarding scenarios, training needs identified, documentation of supervisor guidance on safeguarding matters.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30259, 8, '8.13', 'Documented temperature checks are in place for the medication refrigerator and actions taken if it falls out of range (checks should be completed as a minimum of once per day and temperatures should indicate that medication is stored  between 2 and 8 degrees).', 'Documented temperature checks are in place for the medication refrigerator and actions taken if it falls out of range.', NULL, NULL, 'Documented temperature checks are in place for the medication refrigerator and actions taken if it falls out of range (checks should be completed as a minimum of once per day and temperatures should indicate that medication is stored  between 2 and 8 degrees).', '2025-12-14 18:13:17', NULL, 'Safeguarding audit and quality assurance records showing: regular safeguarding audits, analysis of safeguarding data, trends identified, and service improvements implemented.', 'Safeguarding audit report containing: audit date and scope, number of safeguarding concerns raised, types of abuse identified, outcomes of investigations, themes and trends analysis, comparison with previous periods, action plan for improvements, evidence of learning shared with staff, follow-up audit showing improvements embedded.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30260, 8, '8.16', 'Temperature checks are in place for the designated medication room (or designated storage area) and actions taken if they fall out of range (Temperatures should not go above 25 degrees).', 'Temperature checks are in place for the designated medication room (or designated storage area) and actions taken if they fall out of range', NULL, NULL, 'Temperature checks are in place for the designated medication room (or designated storage area) and actions taken if they fall out of range (Temperatures should not go above 25 degrees).', '2025-12-14 18:13:17', NULL, 'Safeguarding policies covering specific abuse types including: financial abuse prevention, sexual safety, online safety, mate crime, modern slavery, and radicalisation (Prevent).', 'Comprehensive safeguarding framework containing: financial abuse policy (transaction monitoring, appointeeship oversight), sexual safety policy (relationships, consent, privacy), online safety guidance (internet use, social media risks), mate crime awareness, modern slavery indicators and reporting, Prevent duty awareness (radicalisation signs, Channel referral process).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30261, 8, '8.17', 'There is a separate controlled drug register, that is bound, and recording in it is accurate and legible.', 'There is a separate controlled drug register that is bound and recording in it is legible', NULL, NULL, 'There is a separate controlled drug register, that is bound, and recording in it is accurate and legible.', '2025-12-14 18:13:17', NULL, 'Safeguarding partnership working records showing: engagement with local safeguarding adults board, multi-agency meetings, information sharing, and collaborative working.', 'Partnership documentation showing: attendance at safeguarding adults board meetings or sub-groups, multi-agency safeguarding meetings for complex cases, information sharing agreements, joint working protocols with police/social services/health, safeguarding adult reviews (SAR) participation if applicable, implementation of SAR recommendations.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30262, 8, '8.18', 'Medication Administration Records are accurate and the recording legible. including TMAR.', 'Evidence required: Medication is logged as part of daily notes and recorded as a task...', NULL, NULL, 'Medication Administration Records are accurate and the recording legible including TMAR.', '2025-12-14 18:13:17', NULL, 'Service user safeguarding awareness and empowerment records showing: accessible safeguarding information, advocacy support, complaints procedure, and evidence people know how to raise concerns.', 'Service user safeguarding information including: easy-read safeguarding leaflets displayed, residents'' meetings discussing safety and raising concerns, advocacy service information provided, complaints procedure in accessible formats, care plan discussions about safety and who to tell if worried, feedback showing people feel safe and know how to report concerns.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30263, 8, '8.19', 'There is a current signature list of staff that administer medication.To9 include EMARS/ CDs and TMARS ', 'Evidence required: Medications are recorded on the Nourish system and there is a digital record of staff that administe...', NULL, NULL, 'There is a current signature list of staff that administer medication.', '2025-12-14 18:13:17', NULL, 'Visitor safeguarding procedures showing: visitor signing-in arrangements, DBS checks for regular visitors, supervision of visits where safeguarding concerns exist, and visitor code of conduct.', 'Visitor management system containing: visitor sign-in book (name, who visiting, time in/out), DBS check records for regular volunteers/contractors, visitor policy including safeguarding responsibilities, supervised visit arrangements where protection plan requires, visitor code of conduct displayed, safeguarding information for visitors.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30264, 8, '8.20', 'There is a documented process in place to identify where a person can and wishes to safely self-administer their own medication. ', 'There is a documented process in place to identify where a person can and wishes to safely self-administer their own medication', NULL, NULL, 'There is a documented process in place to identify where a person can and wishes to safely self-administer their own medication.', '2025-12-14 18:13:17', NULL, 'Pressure ulcer prevention and management showing this is treated as potential safeguarding concern, including: root cause analysis, safeguarding referral consideration, and prevention strategies.', 'Pressure ulcer management protocol containing: immediate reporting procedure, root cause analysis for all grade 2+ pressure ulcers, safeguarding referral decision-making flowchart, investigation of avoidability, prevention strategies reviewed (repositioning, equipment, nutrition), learning from incidents, pressure ulcer data monitoring and trends.');
