INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30266, 8, '8.22', 'Covert medication protocols are in place with input from relevant medical professionals.', 'Covert medication protocols are in place with input from relevant medical professionals and are regularly reviewed', NULL, NULL, 'Covert medication protocols are in place with input from relevant medical professionals.', '2025-12-14 18:13:17', NULL, 'Safeguarding concerns related to care quality showing: monitoring of care standards, investigation of poor care, safeguarding referrals for neglect/institutional abuse, and service improvements.', 'Quality monitoring showing: care observations identifying concerns (missed care, poor interactions, dignity issues), investigation process, safeguarding referral for institutional abuse/neglect where appropriate, immediate actions (supervision, retraining, disciplinary), service improvements (policy updates, additional training, increased monitoring), follow-up audits confirming improvements.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30267, 8, '8.2', 'There are systems in place to monitor medication stock levels and expiry dates.', 'There are systems in place to monitor medication stock levels and expiry dates and actions taken.', NULL, NULL, 'There are systems in place to monitor medication stock levels and expiry dates.', '2025-12-14 18:13:17', NULL, 'Safeguarding in recruitment showing: safer recruitment practices, DBS checks, reference verification, employment history gaps explored, and barred list checks.', 'Recruitment files containing: enhanced DBS certificates dated within last 3 years or DBS update service checks, two references verified (contact with referee confirmed), employment history gaps discussed at interview and documented, barred list check (DBS certificate shows this), proof of identity verified, right to work confirmed, conditional offer pending satisfactory checks.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30268, 8, '8.3', 'Where the service holds homely remedies medicines, a stock record and generic risk assessment are in place to determine the range of medicines held.', 'Where the service holds homely remedies, there is a stock record and generic risk assessment in place', NULL, NULL, 'Where the service holds homely remedies medicines, a stock record and generic risk assessment are in place to determine the range of medicines held.', '2025-12-14 18:13:17', NULL, 'Allegations against staff procedures showing: suspension/risk assessment, referral to local authority designated officer (LADO), investigation process, and disciplinary procedures.', 'Allegation management documentation containing: immediate risk assessment (suspension or risk-assessed continuation), LADO referral within 24 hours, safeguarding investigation cooperation, parallel disciplinary investigation, investigation outcome, referral to DBS/professional body if substantiated, learning and service improvements, support for alleged victim.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30269, 8, '8.5', 'Staff were observed to handle medicines safely securely and appropriately.', 'Staff were observed to handle medicines safely, securely and appropriately i.e. checking prescribing instructions, checking the ID of the person, using appropriate container to transfer medicines, no secondary dispensing', NULL, NULL, 'Staff were observed to handle medicines safely securely and appropriately.', '2025-12-14 18:13:17', NULL, 'Safeguarding policies addressing organisational abuse including: closed culture prevention, whistleblowing encouragement, quality monitoring, and service user involvement.', 'Organisational safeguarding measures showing: open culture promoted (visitors welcomed, relatives encouraged, external professionals access), whistleblowing policy and awareness, regular quality audits (care observations, medication, environment), service user feedback mechanisms (meetings, surveys, advocates), management visibility and accessibility, external scrutiny welcomed (CQC, commissioners).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30270, 8, '8.6', 'Staff can explain what actions they would take in the event of a medication error. NB Report as RNR /Safeguarding seek medication attention as a minimum ', 'Staff can explain what actions they would take in the event of a medication error', NULL, NULL, 'Staff can explain what actions they would take in the event of a medication error. NB Report as RNR /Safeguarding seek medication attention as a minimum', '2025-12-14 18:13:17', NULL, 'Safeguarding training effectiveness monitoring showing: training completion rates, competency assessment, scenario-based learning, and application in practice.', 'Training effectiveness evidence including: training matrix showing 100% staff completion, post-training competency assessment (quiz, scenarios), supervision discussions applying safeguarding knowledge, safeguarding concerns appropriately identified and reported, audit showing good safeguarding practice, staff survey showing confidence in safeguarding procedures.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30271, 8, '8.7', 'Staff can explain where they would access information in relation to a specific medicine e.g., its usage, side effects of drugs and contra-indications. ', 'Staff can explain where they would access information in relation to a specific medicine e.g., its usage, side effects of drugs and contra-indications', NULL, NULL, 'Staff can explain where they would access information in relation to a specific medicine e.g., its usage, side effects of drugs and contra-indications.', '2025-12-14 18:13:17', NULL, 'Safeguarding continuous improvement showing: lessons learned from incidents, safeguarding adult reviews, best practice implementation, and service development.', 'Continuous improvement documentation showing: lessons learned log from safeguarding incidents, action plans implemented, SAR recommendations actioned if applicable, best practice guidance implemented (NICE, SCIE), safeguarding performance indicators monitored, year-on-year improvements demonstrated, external safeguarding reviews or peer audits, innovation in safeguarding practice.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30273, 9, '9.1', 'There is COSHH register, risk assessments and data sheets in place for any storage and use of hazardous substances.', 'There is COSHH register, risk assessments and data sheets in place for the storage and use of hazardous substances', NULL, NULL, 'There is COSHH register, risk assessments and data sheets in place for any storage and use of hazardous substances.', '2025-12-14 18:13:17', NULL, 'Environmental risk assessments covering: building safety, equipment safety, infection control, fire risks, and specific hazards, with control measures and review dates.', 'Risk assessment file containing: premises risk assessment (trip hazards, lighting, flooring, windows, radiators), equipment risk assessments (hoists, baths, kitchen equipment), infection control risk assessment, fire risk assessment, Legionella risk assessment, specific hazard assessments (chemicals, sharps), control measures for each risk, responsible person assigned, review dates (annual minimum or after incidents).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30274, 9, '9.4', 'There is a fire procedure in place and there is clear and visible signage that advises what actions to take in the event of a fire.', 'There is a fire procedure in place and there is clear and visible signage that advises what actions to take in the event of a fire', NULL, NULL, 'There is a fire procedure in place and there is clear and visible signage that advises what actions to take in the event of a fire.', '2025-12-14 18:13:17', NULL, 'Fire safety arrangements showing: fire risk assessment, fire detection and alarm system, emergency lighting, fire exits, evacuation plan, and Personal Emergency Evacuation Plans (PEEPs).', 'Fire safety documentation containing: fire risk assessment completed by competent person (within last 12 months), fire alarm system servicing records (6-monthly), emergency lighting testing (monthly), fire exit checks (daily), evacuation procedure displayed, assembly point identified, PEEP for each service user (mobility, communication needs, evacuation method), fire drill records (6-monthly minimum).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30275, 9, '9.10', 'There is a record of an annual externally validated fire extinguisher checks.       ', 'There  is evidence of an upto date annual external fire extinguisher checks', NULL, NULL, 'There is a record of an annual externally validated fire extinguisher checks.', '2025-12-14 18:13:17', NULL, 'Fire safety equipment maintenance records showing: fire extinguisher servicing, fire alarm testing, emergency lighting testing, and fire door checks.', 'Fire equipment maintenance logs showing: fire extinguisher annual service certificates with next due date, weekly fire alarm call point testing (different call point each week, logged), monthly emergency lighting test (brief test logged), annual emergency lighting full duration test, fire door checks (self-closing mechanism, seals intact, no wedges), fire blanket in kitchen, records signed and dated.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30276, 9, '9.11', 'There is a record of monthly internally completed fire extingusher checks ', 'There is a record of monthly internal fire extinguisher checks', NULL, NULL, 'There is a record of monthly internally completed fire extingusher checks', '2025-12-14 18:13:17', NULL, 'Fire evacuation drills and staff fire safety training showing: regular fire drills conducted, evacuation times recorded, issues identified and addressed, and staff fire safety training.', 'Fire drill records containing: drill date and time, scenario (day/night, location of fire), evacuation time, staff participation, issues identified (confusion over roles, delayed response), actions taken to address issues, staff fire safety training records (induction and annual refresher), fire warden training for designated staff, PEEP training for staff supporting specific individuals.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30277, 9, '9.12', 'There is a record of an externally validated emergency lighting checks.', 'There is evidence of up to date external  emergency lighting checks', NULL, NULL, 'There is a record of an externally validated emergency lighting checks.', '2025-12-14 18:13:17', NULL, 'Infection prevention and control policy and procedures covering: hand hygiene, PPE use, cleaning schedules, waste management, and outbreak management, reflecting current guidance.', 'IPC policy and procedures containing: policy review date, hand hygiene procedure (7-step technique), PPE guidance (when to use, donning/doffing), cleaning schedules (daily, weekly, deep clean), colour-coded cleaning equipment, waste segregation (clinical, domestic, sharps), laundry procedures, outbreak management plan, isolation procedures, reference to current guidance (NICE, PHE/UKHSA).');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30278, 9, '9.14', 'There is a record of monthly internally completed emergency lighting checks ', 'There is a record of monthly internal emergency lighting checks', NULL, NULL, 'There is a record of monthly internally completed emergency lighting checks', '2025-12-14 18:13:17', NULL, 'Infection control audit records showing: hand hygiene audits, environmental cleanliness audits, PPE compliance checks, and actions taken to address issues.', 'IPC audit documentation showing: monthly hand hygiene observation audits (technique, compliance rates), environmental cleanliness audits (scores for different areas), PPE stock checks and usage monitoring, cleaning schedule compliance checks, action plans for areas scoring below target, re-audit showing improvements, infection control link worker/champion involvement.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30279, 9, '9.15', 'There is a record of 6 monthly fire evacuations/fire drills.   ', 'There is a record of 6 monthly fire evacuations/fire drills evident in the service and any resulting actions identified and implemented.', NULL, NULL, 'There is a record of 6 monthly fire evacuations/fire drills.', '2025-12-14 18:13:17', NULL, 'Infection outbreak management records showing: outbreak identification, control measures implemented, notifications made, investigation, and learning outcomes.', 'Outbreak management file containing: outbreak definition met (2+ linked cases), outbreak control team convened, control measures implemented (isolation, enhanced cleaning, visitor restrictions, staff cohorting), notifications to PHE/UKHSA, CQC, commissioners, outbreak investigation findings, microbiological results, outbreak declared over criteria, lessons learned and service improvements.');
INSERT INTO `complianceQuestions` (`id`, `sectionId`, `questionNumber`, `questionText`, `standardDescription`, `tooltip`, `requiredDocuments`, `guidance`, `createdAt`, `conditionalLogic`, `evidenceRequirement`, `exampleEvidence`) VALUES (30280, 9, '9.16', 'There is a record of weekly fire alarm checks.', 'There are complete records of weekly fire alarm checks evident in the service.', NULL, NULL, 'There is a record of weekly fire alarm checks.', '2025-12-14 18:13:17', NULL, 'Water safety management showing: Legionella risk assessment, water temperature monitoring, system flushing records, and remedial actions.', 'Water safety documentation containing: Legionella risk assessment by competent person (reviewed annually), water temperature monitoring logs (hot water >50°C at outlets, cold water <20°C), weekly flushing of little-used outlets, monthly shower head descaling, annual tank inspection, remedial actions for out-of-range temperatures, responsible person assigned.');
